mapper.select:
select
,
from CASE_BASIC where CASE_ID = #{caseId,jdbcType=VARCHAR}

SELECT COUNT(1)
FROM CASE_BASIC A WHERE
A.PROC_KEY=#{procKey,jdbcType=VARCHAR}

	AND A.CASE_NO LIKE
	CONCAT(CONCAT('%',#{caseNo,jdbcType=VARCHAR}),'%')


	AND A.CASE_NAME LIKE
	CONCAT(CONCAT('%',#{caseName,jdbcType=VARCHAR}),'%')


		AND A.INPUT_TIME>=#{startTime,jdbcType=TIMESTAMP}


		AND A.INPUT_TIME<(#{endTime,jdbcType=TIMESTAMP} + INTERVAL '1' DAY)


	AND A.CASE_STATE = #{caseState,jdbcType=VARCHAR}


	AND A.INPUTER = #{inputer}

SELECT
A.CASE_ID,A.CASE_NO,A.CASE_NAME,A.CASE_STATE,A.INPUT_TIME,A.LATEST_POCESS_TIME,S.CHUFA_STATE,A.CASE_TYPE,A.VERSION_NO
FROM CASE_BASIC A,CASE_STATE S WHERE A.CASE_ID=S.CASE_ID AND A.PROC_KEY=#{procKey}

	AND A.CASE_NO LIKE
	CONCAT(CONCAT('%',#{caseNo,jdbcType=VARCHAR}),'%')


	AND A.CASE_NAME LIKE
	CONCAT(CONCAT('%',#{caseName,jdbcType=VARCHAR}),'%')


		AND A.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}


		AND A.INPUT_TIME < (#{endTime,jdbcType=TIMESTAMP} + INTERVAL '1' DAY)


	AND A.CASE_STATE = #{caseState}


	AND A.INPUTER = #{inputer}

ORDER BY A.LATEST_POCESS_TIME DESC

		SELECT CB.CASE_ID,
           CB.CASE_NO,
           CB.CASE_NAME,
           CB.CASE_STATE,
           PCE.CHUFA_TIME,
           CB.INPUT_TIME,
           CB.LATEST_POCESS_TIME,
           CB.PROC_KEY,
           U.ORG_NAME
   		 FROM 


				(SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.INPUT_TIME,C.AMOUNT_INVOLVED,C.IS_SUSPECTED_CRIMINAL,
				 C.ACCEPT_UNIT,C.CASE_STATE,C.RECORD_SRC,C.LATEST_POCESS_TIME,C.PROC_KEY,C.CASE_TYPE,C.IDENTIFY_TYPE,
				 C.IS_LOWER_LIMIT_MONEY
					FROM CASE_BASIC C,
					(SELECT S.CASE_ID
				      FROM CASE_STEP S, ORGANISE O
				      WHERE S.TARGET_ORG_ID = O.ORG_CODE
				      AND S.TARGET_ORG_TYPE = '3'
				      AND O.POLICE_TYPE=2
				      GROUP BY CASE_ID 
				     ) SO
				 WHERE C.CASE_ID=SO.CASE_ID) CB


				(SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.INPUT_TIME,C.AMOUNT_INVOLVED,C.IS_SUSPECTED_CRIMINAL,
				 C.ACCEPT_UNIT,C.CASE_STATE,C.RECORD_SRC,C.LATEST_POCESS_TIME,C.PROC_KEY,C.CASE_TYPE,C.IDENTIFY_TYPE,
				 C.IS_LOWER_LIMIT_MONEY
				 FROM CASE_BASIC C LEFT JOIN 
				   (SELECT S.CASE_ID,O.POLICE_TYPE FROM CASE_STEP S, ORGANISE O
				    WHERE S.TARGET_ORG_ID = O.ORG_CODE
				    AND S.TARGET_ORG_TYPE = '3'
				    GROUP BY CASE_ID,O.POLICE_TYPE) SO
				    on C.CASE_ID = SO.CASE_ID
				 WHERE (SO.POLICE_TYPE = 1 OR SO.POLICE_TYPE IS NULL)) CB


				CASE_BASIC CB 


		  ,CASE_STATE CS,PENALTY_CASE_FORM PCE,V_USER_ORGANISE_DISTRICT U
    	 WHERE CB.CASE_ID=CS.CASE_ID
    	 AND CB.CASE_ID=PCE.CASE_ID(+)
   		 AND CB.INPUTER=U.USER_ID

			AND U.DISTRICT_CODE LIKE #{districtId}||'%'


			AND U.DISTRICT_CODE LIKE #{shortDistrictId}||'%'


			  AND U.INDUSTRY_TYPE= #{industryType}


			  AND U.ORG_PATH LIKE #{orgPath} ||'%'


			AND CB.CASE_NO LIKE CONCAT(CONCAT('%',#{caseNo}),'%')


			AND CB.CASE_NAME LIKE CONCAT(CONCAT('%',#{caseName}),'%')


			AND PCE.IS_DQDJ =#{isDqdj}



				AND CS.CHUFA_STATE=#{chufaState}


				AND CS.YISONG_STATE = #{yisongState}


				AND CS.LIAN_STATE>=#{lianState}


				AND CS.LIAN_STATE=#{lianState}


				AND CS.LIAN_STATE=#{lianState}


				AND CS.LIAN_STATE=#{lianState}


				AND CS.DAIBU_STATE=#{daibuState}


				AND CS.QISU_STATE=#{qisuState}


				AND CS.PANJUE_STATE=#{panjueState}


				AND CS.JIEAN_STATE=#{jieanState}


				AND CS.YISONG_STATE IN (2,3)


				AND CS.REQ_EXPLAIN_STATE=#{reqExplainState}


				AND CB.CASE_STATE=#{caseState}


				AND CB.CASE_TYPE=#{caseType}


				AND CS.XINGZHENGLIAN_STATE=#{xingzhenglianState}


				AND CS.DETENTION_STATE=#{detentionState}


				AND CS.LIAN_SUP_STATE>=1




			   AND CB.INPUT_TIME >= TO_DATE(#{startTime},'yyyy-MM')




			     AND CB.INPUT_TIME  < TO_DATE(#{endTime},'yyyy-MM') + INTERVAL '1' MONTH



			AND TO_CHAR(CB.INPUT_TIME,'yyyy') = #{yearCode}



			AND U.INDUSTRY_TYPE IS NOT NULL


			AND PCE.CHUFA_TIMES>#{chufatimescount}


			AND PCE.IS_BEYOND_EIGHTY=#{beyondeightycount}


			AND PCE.IDENTIFY_TYPE != #{identifycount} AND PCE.IDENTIFY_TYPE IS NOT NULL


			AND PCE.IS_LOWER_LIMIT_MONEY=#{lowerlimitmoneycount}


			AND PCE.IS_DESCUSS=#{descusscount}


			AND PCE.IS_SERIOUS_CASE=#{seriouscasecount}


			AND CB.IS_SUSPECTED_CRIMINAL=1


			AND CS.CHUFA_STATE= 2 




				AND TO_CHAR(CB.INPUT_TIME,'MM') >= 01
				AND TO_CHAR(CB.INPUT_TIME,'MM') <= 03


				AND TO_CHAR(CB.INPUT_TIME,'MM') >= 04
				AND TO_CHAR(CB.INPUT_TIME,'MM') <= 06


				AND TO_CHAR(CB.INPUT_TIME,'MM') >= 07
				AND TO_CHAR(CB.INPUT_TIME,'MM') <= 09


				AND TO_CHAR(CB.INPUT_TIME,'MM') >= 10
				AND TO_CHAR(CB.INPUT_TIME,'MM') <= 12



			AND TO_CHAR(CB.INPUT_TIME,'yyyyMM') = #{monthCode}

		ORDER BY CB.LATEST_POCESS_TIME DESC,CB.CASE_ID

		 SELECT COUNT(CB.CASE_ID) 
		 FROM 


				(SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.INPUT_TIME,C.AMOUNT_INVOLVED,C.IS_SUSPECTED_CRIMINAL,
				 C.ACCEPT_UNIT,C.CASE_STATE,C.RECORD_SRC,C.LATEST_POCESS_TIME,C.PROC_KEY,C.CASE_TYPE,C.IDENTIFY_TYPE,
				 C.IS_LOWER_LIMIT_MONEY
					FROM CASE_BASIC C,
					(SELECT S.CASE_ID
				      FROM CASE_STEP S, ORGANISE O
				      WHERE S.TARGET_ORG_ID = O.ORG_CODE
				      AND S.TARGET_ORG_TYPE = '3'
				      AND O.POLICE_TYPE=2
				      GROUP BY CASE_ID 
				     ) SO
				 WHERE C.CASE_ID=SO.CASE_ID) CB


				(SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.INPUT_TIME,C.AMOUNT_INVOLVED,C.IS_SUSPECTED_CRIMINAL,
				 C.ACCEPT_UNIT,C.CASE_STATE,C.RECORD_SRC,C.LATEST_POCESS_TIME,C.PROC_KEY,C.CASE_TYPE,C.IDENTIFY_TYPE,
				 C.IS_LOWER_LIMIT_MONEY
				 FROM CASE_BASIC C LEFT JOIN 
				   (SELECT S.CASE_ID,O.POLICE_TYPE FROM CASE_STEP S, ORGANISE O
				    WHERE S.TARGET_ORG_ID = O.ORG_CODE
				    AND S.TARGET_ORG_TYPE = '3'
				    GROUP BY CASE_ID,O.POLICE_TYPE) SO
				    on C.CASE_ID = SO.CASE_ID
				 WHERE (SO.POLICE_TYPE = 1 OR SO.POLICE_TYPE IS NULL)) CB


				CASE_BASIC CB 


		 ,CASE_STATE CS,V_USER_ORGANISE_DISTRICT U,PENALTY_CASE_FORM PCE
    	 WHERE CB.CASE_ID=CS.CASE_ID
    	 AND CB.CASE_ID=PCE.CASE_ID(+)
   		 AND CB.INPUTER=U.USER_ID

			AND U.DISTRICT_CODE LIKE #{districtId}||'%' 


			AND U.DISTRICT_CODE LIKE #{shortDistrictId}||'%'


			  AND U.INDUSTRY_TYPE= #{industryType}


			  AND U.ORG_PATH LIKE #{orgPath} ||'%'


			AND CB.CASE_NO LIKE CONCAT(CONCAT('%',#{caseNo}),'%')


			AND CB.CASE_NAME LIKE CONCAT(CONCAT('%',#{caseName}),'%')


			AND PCE.IS_DQDJ =#{isDqdj}



				AND CS.CHUFA_STATE=#{chufaState}


				AND CS.YISONG_STATE = #{yisongState}


				AND CS.LIAN_STATE>=#{lianState}


				AND CS.LIAN_STATE=#{lianState}


				AND CS.LIAN_STATE=#{lianState}


				AND CS.LIAN_STATE=#{lianState}


				AND CS.DAIBU_STATE=#{daibuState}


				AND CS.QISU_STATE=#{qisuState}


				AND CS.PANJUE_STATE=#{panjueState}


				AND CS.JIEAN_STATE=#{jieanState}


				AND CS.YISONG_STATE IN (2,3)


				AND CS.REQ_EXPLAIN_STATE=#{reqExplainState}


				AND CB.CASE_STATE=#{caseState}


				AND CB.CASE_TYPE=#{caseType}


				AND CS.XINGZHENGLIAN_STATE=#{xingzhenglianState}


				AND CS.DETENTION_STATE=#{detentionState}


				AND CS.LIAN_SUP_STATE>=1




			   AND CB.INPUT_TIME >= TO_DATE(#{startTime},'yyyy-MM')




			     AND CB.INPUT_TIME  < TO_DATE(#{endTime},'yyyy-MM') + INTERVAL '1' MONTH



			AND TO_CHAR(CB.INPUT_TIME,'yyyy') = #{yearCode}



				AND TO_CHAR(CB.INPUT_TIME,'MM') >= 01
				AND TO_CHAR(CB.INPUT_TIME,'MM') <= 03


				AND TO_CHAR(CB.INPUT_TIME,'MM') >= 04
				AND TO_CHAR(CB.INPUT_TIME,'MM') <= 06


				AND TO_CHAR(CB.INPUT_TIME,'MM') >= 07
				AND TO_CHAR(CB.INPUT_TIME,'MM') <= 09


				AND TO_CHAR(CB.INPUT_TIME,'MM') >= 10
				AND TO_CHAR(CB.INPUT_TIME,'MM') <= 12



			AND TO_CHAR(CB.INPUT_TIME,'yyyyMM') = #{monthCode}



			AND U.INDUSTRY_TYPE IS NOT NULL


			AND PCE.CHUFA_TIMES>1


			AND PCE.IS_BEYOND_EIGHTY=#{beyondeightycount}


			AND PCE.IDENTIFY_TYPE != #{identifycount} AND PCE.IDENTIFY_TYPE IS NOT NULL


			AND PCE.IS_LOWER_LIMIT_MONEY=#{lowerlimitmoneycount}


			AND PCE.IS_DESCUSS=#{descusscount}


			AND PCE.IS_SERIOUS_CASE=#{seriouscasecount}


			AND CB.IS_SUSPECTED_CRIMINAL=1 


			AND CS.CHUFA_STATE=2

SELECT CB.CASE_ID, CB.CASE_NO, CB.CASE_NAME, CB.CASE_STATE, PCE.CHUFA_TIME, CB.INPUT_TIME, CB.LATEST_POCESS_TIME, CB.PROC_KEY FROM CASE_BASIC CB,CASE_STATE CS,PENALTY_CASE_EXT PCE,V_USER_ORGANISE_DISTRICT U WHERE CB.CASE_ID=CS.CASE_ID AND CB.CASE_ID=PCE.CASE_ID(+) AND CB.INPUTER=U.user_id
AND U.district_code IN
#{districtList}
AND CB.INPUTER IN (SELECT A.USER_ID FROM USER_ A WHERE A.ORG_ID IN
#{orgCode}
)
AND (CS.YISONG_STATE = 2 OR CS.YISONG_STATE = 3)
AND CB.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}
AND CB.INPUT_TIME < #{endTime,jdbcType=TIMESTAMP}
AND to_char(CB.INPUT_TIME,'yyyy') = #{yearCode}
AND to_char(CB.INPUT_TIME,'MM') >= 01 AND to_char(CB.INPUT_TIME,'MM') <= 03
AND to_char(CB.INPUT_TIME,'MM') >= 04 AND to_char(CB.INPUT_TIME,'MM') <= 06
AND to_char(CB.INPUT_TIME,'MM') >= 07 AND to_char(CB.INPUT_TIME,'MM') <= 09
AND to_char(CB.INPUT_TIME,'MM') >= 10 AND to_char(CB.INPUT_TIME,'MM') <= 12
AND to_char(CB.INPUT_TIME,'yyyyMM') = #{monthCode}
ORDER BY CB.LATEST_POCESS_TIME DESC,CB.CASE_ID

		SELECT COUNT(CB.CASE_ID)
		FROM CASE_BASIC CB,CASE_STATE CS,V_USER_ORGANISE_DISTRICT U
    	WHERE CB.CASE_ID=CS.CASE_ID
    	AND CB.INPUTER=U.user_id


					AND U.district_code IN 

	       				#{districtList}



					AND CB.INPUTER IN (SELECT A.USER_ID FROM USER_ A WHERE 
    				A.ORG_ID IN  

	       				#{orgCode}

					)


				AND (CS.YISONG_STATE = 2 OR CS.YISONG_STATE = 3)


			   	AND CB.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}





			     AND CB.INPUT_TIME  < #{endTime,jdbcType=TIMESTAMP}





			AND to_char(CB.INPUT_TIME,'yyyy') = #{yearCode}



				AND to_char(CB.INPUT_TIME,'MM') >= 01
				AND to_char(CB.INPUT_TIME,'MM') <= 03


				AND to_char(CB.INPUT_TIME,'MM') >= 04
				AND to_char(CB.INPUT_TIME,'MM') <= 06


				AND to_char(CB.INPUT_TIME,'MM') >= 07
				AND to_char(CB.INPUT_TIME,'MM') <= 09


				AND to_char(CB.INPUT_TIME,'MM') >= 10
				AND to_char(CB.INPUT_TIME,'MM') <= 12



			AND to_char(CB.INPUT_TIME,'yyyyMM') = #{monthCode}

SELECT CB.CASE_ID,CB.CASE_NO,CB.CASE_NAME,CB.CASE_STATE,PCE.CHUFA_TIME,CB.INPUT_TIME,CB.LATEST_POCESS_TIME, CB.PROC_KEY,UOD.ORG_NAME FROM
(SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.INPUT_TIME,C.AMOUNT_INVOLVED, C.ACCEPT_UNIT,C.CASE_STATE,C.RECORD_SRC,C.LATEST_POCESS_TIME,C.PROC_KEY,C.CASE_TYPE FROM CASE_BASIC C, (SELECT S.CASE_ID FROM CASE_STEP S, ORGANISE O WHERE S.TARGET_ORG_ID = O.ORG_CODE AND S.TARGET_ORG_TYPE = '3' AND O.POLICE_TYPE=2 GROUP BY CASE_ID ) SO WHERE C.CASE_ID=SO.CASE_ID) CB
(SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.INPUT_TIME,C.AMOUNT_INVOLVED, C.ACCEPT_UNIT,C.CASE_STATE,C.RECORD_SRC,C.LATEST_POCESS_TIME,C.PROC_KEY,C.CASE_TYPE FROM CASE_BASIC C, (SELECT S.CASE_ID,MAX(O.POLICE_TYPE)AS POLICE_TYPE FROM CASE_STEP S, ORGANISE O WHERE S.TARGET_ORG_ID = O.ORG_CODE AND S.TARGET_ORG_TYPE = '3' GROUP BY CASE_ID ) SO WHERE C.CASE_ID=SO.CASE_ID AND (SO.POLICE_TYPE=1 OR SO.POLICE_TYPE IS NULL)) CB
CASE_BASIC CB
,PENALTY_CASE_EXT PCE, (SELECT A.CASE_ID,A.ACCUSE_ID FROM CASE_ACCUSE A UNION SELECT B.CASE_ID,B.ACCUSE_ID FROM XIANYIREN_ACCUSE B GROUP BY ACCUSE_ID,CASE_ID ) CA, V_USER_ORGANISE_DISTRICT UOD WHERE CB.CASE_ID=PCE.CASE_ID(+) AND CB.CASE_ID=CA.CASE_ID AND CB.INPUTER=UOD.USER_ID
AND UOD.DISTRICT_CODE like #{districtId} ||'%'
AND CA.ACCUSE_ID= #{accuseId}
AND CB.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}
AND CB.INPUT_TIME < #{endTime,jdbcType=TIMESTAMP}
ORDER BY CB.LATEST_POCESS_TIME DESC,CB.CASE_ID

SELECT COUNT(CB.CASE_ID) FROM
(SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.INPUT_TIME,C.AMOUNT_INVOLVED, C.ACCEPT_UNIT,C.CASE_STATE,C.RECORD_SRC,C.LATEST_POCESS_TIME,C.PROC_KEY,C.CASE_TYPE FROM CASE_BASIC C, (SELECT S.CASE_ID FROM CASE_STEP S, ORGANISE O WHERE S.TARGET_ORG_ID = O.ORG_CODE AND S.TARGET_ORG_TYPE = '3' AND O.POLICE_TYPE=2 GROUP BY CASE_ID ) SO WHERE C.CASE_ID=SO.CASE_ID) CB
(SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.INPUT_TIME,C.AMOUNT_INVOLVED, C.ACCEPT_UNIT,C.CASE_STATE,C.RECORD_SRC,C.LATEST_POCESS_TIME,C.PROC_KEY,C.CASE_TYPE FROM CASE_BASIC C, (SELECT S.CASE_ID,MAX(O.POLICE_TYPE)AS POLICE_TYPE FROM CASE_STEP S, ORGANISE O WHERE S.TARGET_ORG_ID = O.ORG_CODE AND S.TARGET_ORG_TYPE = '3' GROUP BY CASE_ID ) SO WHERE C.CASE_ID=SO.CASE_ID AND (SO.POLICE_TYPE=1 OR SO.POLICE_TYPE IS NULL)) CB
CASE_BASIC CB
, (SELECT A.CASE_ID,A.ACCUSE_ID FROM CASE_ACCUSE A UNION SELECT B.CASE_ID,B.ACCUSE_ID FROM XIANYIREN_ACCUSE B GROUP BY ACCUSE_ID,CASE_ID ) CA, V_USER_ORGANISE_DISTRICT UOD WHERE CB.CASE_ID=CA.CASE_ID AND CB.INPUTER=UOD.USER_ID
AND UOD.DISTRICT_CODE like #{districtId} ||'%'
AND CA.ACCUSE_ID= #{accuseId}
AND CB.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}
AND CB.INPUT_TIME < #{endTime,jdbcType=TIMESTAMP}

SELECT C.CASE_ID, C.CASE_NO, C.CASE_NAME, C.CASE_STATE, PCE.CHUFA_TIME, C.INPUT_TIME, C.LATEST_POCESS_TIME, C.PROC_KEY FROM CASE_BASIC C, DQDJ_CASE_CATEGORY CC,PENALTY_CASE_EXT PCE WHERE C.CASE_ID = CC.CASE_ID AND C.CASE_ID = PCE.CASE_ID(+) AND CC.CATEGORY_ID = #{category_id}
AND C.INPUTER IN (SELECT U.USER_ID FROM ORGANISE O,USER_ U WHERE O.ORG_CODE=U.ORG_ID AND O.ORG_CODE IN
#{orgList}
)
AND C.INPUTER IN (SELECT U.USER_ID FROM ORGANISE O,USER_ U WHERE O.ORG_CODE=U.ORG_ID AND O.DISTRICT_CODE LIKE concat(#{districtCode},'%'))
AND C.INPUT_TIME >= to_date(#{startTime},'yyyy-MM-dd')
AND C.INPUT_TIME < to_date(#{endTime},'yyyy-MM-dd') + interval '1' day
ORDER BY C.INPUT_TIME DESC

SELECT COUNT(C.CASE_ID) FROM CASE_BASIC C, DQDJ_CASE_CATEGORY CC WHERE C.CASE_ID = CC.CASE_ID AND CC.CATEGORY_ID = #{category_id}
AND C.INPUTER IN (SELECT U.USER_ID FROM ORGANISE O,USER_ U WHERE O.ORG_CODE=U.ORG_ID AND O.ORG_CODE IN
#{orgList}
)
AND C.INPUTER IN (SELECT U.USER_ID FROM ORGANISE O,USER_ U WHERE O.ORG_CODE=U.ORG_ID AND O.DISTRICT_CODE LIKE concat(#{districtCode},'%'))
AND C.INPUT_TIME >= to_date(#{startTime},'yyyy-MM-dd')
AND C.INPUT_TIME < to_date(#{endTime},'yyyy-MM-dd') + interval '1' day
ORDER BY C.INPUT_TIME DESC

SELECT C.CASE_ID, C.CASE_NO, C.CASE_NAME, C.CASE_STATE, PCE.CHUFA_TIME, C.INPUT_TIME, C.LATEST_POCESS_TIME, C.PROC_KEY FROM CASE_BASIC C, DQDJ_CASE_CATEGORY CC,PENALTY_CASE_EXT PCE WHERE C.CASE_ID = CC.CASE_ID AND C.CASE_ID = PCE.CASE_ID(+) AND CC.CATEGORY_ID = #{category_id}
AND C.INPUTER IN (SELECT U.USER_ID FROM ORGANISE O,DISTRICT D,USER_ U WHERE O.ORG_CODE=U.ORG_ID AND D.DISTRICT_CODE=O.DISTRICT_CODE AND D.DISTRICT_CODE IN
#{districtCode}
)
AND C.INPUT_TIME >= to_date(#{startTime},'yyyy-MM-dd')
AND C.INPUT_TIME < to_date(#{endTime},'yyyy-MM-dd') + interval '1' day
ORDER BY C.INPUT_TIME DESC

SELECT COUNT(C.CASE_ID) FROM CASE_BASIC C, DQDJ_CASE_CATEGORY CC WHERE C.CASE_ID = CC.CASE_ID AND CC.CATEGORY_ID = #{category_id}
AND C.INPUTER IN (SELECT U.USER_ID FROM ORGANISE O,DISTRICT D,USER_ U WHERE O.ORG_CODE=U.ORG_ID AND D.DISTRICT_CODE=O.DISTRICT_CODE AND D.DISTRICT_CODE IN
#{districtCode}
)
AND C.INPUT_TIME >= to_date(#{startTime},'yyyy-MM-dd')
AND C.INPUT_TIME < to_date(#{endTime},'yyyy-MM-dd') + interval '1' day
ORDER BY C.INPUT_TIME DESC

		select CASE_ID,CASE_NO,CASE_NAME,CASE_STATE,INPUT_TIME,PROC_KEY,LATEST_POCESS_TIME,O.ORG_NAME AS INPUT_UNIT_NAME from case_basic c,user_ u,organise o
		where c.inputer=u.user_id and u.org_id=o.org_code

				and case_no like '%${caseNo}%'


				and case_name like '%${caseName}%'


				and proc_key=#{procKey}


				and input_type=#{inputType}


				and o.district_code like '${districtCode}%'


                and o.org_path like '${orgPath}%'


                AND  O.ORG_CODE = #{orgId}

		order by latest_pocess_time desc

		select count(*) from case_basic c,user_ u,organise o
		where c.inputer=u.user_id and u.org_id=o.org_code

				and case_no like '%${caseNo}%'


				and case_name like '%${caseName}%'


				and proc_key=#{procKey}


				and input_type=#{inputType}


				and o.district_code like '${districtCode}%'


                and o.org_path like '${orgPath}%'


                AND  O.ORG_CODE = #{orgId}

select count(*) from case_basic c,v_user_organise_district a where c.inputer=a.user_id and case_no =#{caseNo}
and (a.org_code=#{orgCode} or a.up_org_code =#{orgCode} )
and a.district_code like concat(#{districtCode},'%')

SELECT DC.CASE_ID,DC.CASE_NO,DC.CASE_NAME,DC.CASE_STATE, U.USER_NAME AS USER_NAME,DC.PROC_KEY, A.CREATE_TIME_ AS CREATE_TIME FROM (SELECT DISTINCT T.* FROM ACT_RU_TASK T INNER JOIN ACT_RU_IDENTITYLINK I ON I.TASK_ID_ = T.ID_ WHERE T.ASSIGNEE_ IS NULL AND I.TYPE_ = 'candidate' AND I.GROUP_ID_ = #{candidateGroup,jdbcType=VARCHAR} UNION SELECT DISTINCT T.* FROM ACT_RU_TASK T WHERE T.ASSIGNEE_ = #{userID,jdbcType=VARCHAR}) A, (SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.INPUTER,C.CASE_STATE,C.PROC_KEY, CP.PROC_INST_ID,CP.PROC_DEF_ID, D.DT_NAME AS DT_NAME FROM DICTIONARY_ D, CASE_BASIC C, CASE_PROCESS CP WHERE D.DT_CODE = C.CASE_STATE AND C.CASE_ID = CP.CASE_ID AND D.GROUP_CODE = 'chufaProcState') DC, USER_ U, ORGANISE O WHERE A.PROC_INST_ID_ = DC.PROC_INST_ID AND DC.INPUTER = U.USER_ID AND U.ORG_ID = O.ORG_CODE AND DC.PROC_DEF_ID = #{proc_def_id,jdbcType=VARCHAR} AND A.TASK_DEF_KEY_ = #{task_def_id,jdbcType=VARCHAR}
AND A.CREATE_TIME_ < DATEDIFFERENCE(#{due_date,jdbcType=VARCHAR})
ORDER BY CREATE_TIME DESC

select  c.case_id,c.case_no,c.case_name,c.case_state,c.input_time,c.proc_key,c.inputer,r.read_state as readState from case_basic c,case_record r
 where c.case_id=r.case_id and c.proc_key=r.proc_key and r.user_id=#{userId}

    and r.read_state=#{readState}

 order by r.read_state asc,input_time desc

select count(*) from case_basic c,case_record r
 where c.case_id=r.case_id and c.proc_key=r.proc_key
 and r.user_id=#{userId}

      and r.read_state=#{readState}

 order by input_time desc

		SELECT CB.CASE_ID,
           CB.CASE_NO,
           CB.CASE_NAME,
           CB.CASE_STATE,
           CB.YISONG_TIME,
           CB.INPUT_TIME,
           CB.LATEST_POCESS_TIME,
           CB.PROC_KEY
   		 FROM CASE_BASIC CB,CASE_STATE CS,V_USER_ORGANISE_DISTRICT U
    	 WHERE CB.CASE_ID=CS.CASE_ID
   		 AND CB.INPUTER=U.USER_ID


				AND U.district_code IN 

					#{districtList}



				AND CB.INPUTER IN (SELECT A.USER_ID FROM USER_ A WHERE 
        		A.ORG_ID IN 

					#{orgCode}

				)





				AND CS.YISONG_STATE = #{yisongState}


				AND CS.LIAN_STATE=#{lianState}


				AND CS.DAIBU_STATE=#{daibuState}


				AND CS.QISU_STATE=#{qisuState}


				AND CS.PANJUE_STATE=#{panjueState}


				AND CS.JIEAN_STATE=#{jieanState}


				AND CS.YISONG_STATE IN (2,3)


