select a.*,b.case_detail_name from ( SELECT DISTINCT CB1.CASE_ID,CB1.CASE_NO,CB1.CASE_NAME,CB1.INPUTER,CB1.INPUT_TIME,CB1.CHUFA_TIME,CB1.ANFA_ADDRESS,CB1.ANFA_TIME, CB1.AMOUNT_INVOLVED,CB1.ORG_NAME,CB1.identify_type, CB1.CASE_STATE,CB1.GOODS_INVOLVED,CB1.XIANYIREN_NAME,CB1.COMPANY_NAME FROM (SELECT CB.CASE_ID,CB.CASE_NO,CB.CASE_NAME,CB.INPUTER,CB.INPUT_TIME,PCE.CHUFA_TIME,PCE.ANFA_ADDRESS,PCE.ANFA_TIME, CB.AMOUNT_INVOLVED,O.ORG_NAME,CB.identify_type,CB.CASE_STATE,CB.GOODS_INVOLVED, CX.IDS_NO,CX."NAME" AS XIANYIREN_NAME,CC."NAME" AS COMPANY_NAME FROM CASE_BASIC CB,CASE_STATE CST,PENALTY_CASE_EXT PCE,CASE_XIANYIREN CX,CASE_COMPANY CC,USER_ U,ORGANISE O WHERE CB.PROC_KEY = 'chufaProc' AND CB.CASE_ID = CST.CASE_ID AND CB.CASE_ID =PCE.CASE_ID(+) AND CB.CASE_ID = CX.CASE_ID(+) AND CB.CASE_ID= CC.CASE_ID(+) AND CB.CASE_TYPE IN (1,2) AND CB.INPUTER = U.USER_ID AND U.ORG_ID = O.ORG_CODE
AND cb.case_name like concat('%',concat(#{caseName},'%'))
) CB1, (SELECT CB.CASE_ID,CB.CASE_NO,CB.CASE_NAME,CB.INPUTER,CB.INPUT_TIME,PCE.CHUFA_TIME,PCE.ANFA_ADDRESS,PCE.ANFA_TIME, CB.AMOUNT_INVOLVED,O.ORG_NAME,CB.identify_type,CB.CASE_STATE,CB.GOODS_INVOLVED, CX.IDS_NO,CX."NAME" AS XIANYIREN_NAME,CC."NAME" AS COMPANY_NAME FROM CASE_BASIC CB,CASE_STATE CST,PENALTY_CASE_EXT PCE,CASE_XIANYIREN CX,CASE_COMPANY CC,USER_ U,ORGANISE O WHERE CB.PROC_KEY = 'chufaProc' AND CB.CASE_ID = CST.CASE_ID AND CB.CASE_ID =PCE.CASE_ID(+) AND CB.CASE_ID = CX.CASE_ID(+) AND CB.CASE_ID= CC.CASE_ID(+) AND CB.CASE_TYPE IN (1,2) AND CB.INPUTER = U.USER_ID AND U.ORG_ID = O.ORG_CODE
AND cb.case_name like concat('%',concat(#{caseName},'%'))
) CB2 WHERE cb1.CASE_ID != cb2.CASE_ID and CB1.INPUTER = CB2.INPUTER
) a left join case_basic b on a.case_id = b.case_id order by a.INPUTER,a.ANFA_TIME desc,a.anfa_address asc

