	SELECT C.CASE_NO,C.CASE_NAME,PCE.IS_LOWER_LIMIT_MONEY,C.IS_SUSPECTED_CRIMINAL,PCE.IDENTIFY_TYPE,
	PCE.IS_DESCUSS,PCE.IS_BEYOND_EIGHTY,PCE.IS_SERIOUS_CASE,PCE.CHUFA_TIMES 
    FROM  CASE_BASIC C,PENALTY_CASE_FORM PCE,USER_ U,ORGANISE O,CASE_STATE S
     WHERE C.CASE_ID = PCE.CASE_ID(+)
     AND C.INPUTER = U.USER_ID
     AND U.ORG_ID = O.ORG_CODE
     AND C.CASE_ID = S.CASE_ID
     AND C.CASE_STATE = 2
     AND S.YISONG_STATE = 1

		 AND  O.DISTRICT_CODE LIKE #{districtId}||'%'


		 and c.input_time >= #{minCaseInputTime,jdbcType=TIMESTAMP}


		 and c.input_time < (#{maxCaseInputTime,jdbcType=TIMESTAMP} +   interval   '1'  day)

	 and (1 = 2 

				or pce.is_descuss=#{isDescuss}


				 or pce.chufa_times > #{chufaTimes} 


				or pce.is_serious_case=#{isSeriousCase,jdbcType=DECIMAL}


				or pce.is_beyond_eighty=#{isBeyondEighty,jdbcType=DECIMAL}


				or pce.IS_LOWER_LIMIT_MONEY=#{isLowerLimitMoney,jdbcType=DECIMAL}


				or pce.IDENTIFY_TYPE <6 


				or pce.IDENTIFY_TYPE =6 

	 )

		and pce.IS_DESCUSS = #{isDescussQ,jdbcType=DECIMAL}


		 and pce.CHUFA_TIMES > #{chufaTimesQ,jdbcType=DECIMAL} 


		and pce.is_serious_case=#{isSeriousCaseQ,jdbcType=DECIMAL}


		and pce.is_beyond_eighty=#{isBeyondEightyQ,jdbcType=DECIMAL}


		and pce.IS_LOWER_LIMIT_MONEY=#{isLowerLimitMoneyQ,jdbcType=DECIMAL}


		and pce.IDENTIFY_TYPE <6 


		and pce.IDENTIFY_TYPE =6

	SELECT COUNT(1)
    FROM  CASE_BASIC C,PENALTY_CASE_FORM PCE,USER_ U,ORGANISE O,CASE_STATE S
     WHERE C.CASE_ID = PCE.CASE_ID
     AND C.INPUTER=U.USER_ID
     AND U.ORG_ID=O.ORG_CODE
     AND C.CASE_ID = S.CASE_ID 
     AND C.CASE_STATE = 2
     AND S.YISONG_STATE = 1

		 AND  O.DISTRICT_CODE LIKE #{districtId}||'%'


		 and c.input_time >= #{minCaseInputTime,jdbcType=TIMESTAMP}


		 and c.input_time < (#{maxCaseInputTime,jdbcType=TIMESTAMP} +   interval   '1'  day)

	 and (1 = 2 

				or pce.is_descuss=#{isDescuss}


				 or pce.chufa_times > #{chufaTimes} 


				or pce.is_serious_case=#{isSeriousCase}


				or pce.is_beyond_eighty=#{isBeyondEighty}


				or pce.IS_LOWER_LIMIT_MONEY=#{isLowerLimitMoney}


				or pce.IDENTIFY_TYPE <6 


				or pce.IDENTIFY_TYPE =6 

	 )

		and pce.IS_DESCUSS = #{isDescussQ}


		 and pce.CHUFA_TIMES > #{chufaTimesQ} 


		and pce.is_serious_case=#{isSeriousCaseQ}


		and pce.is_beyond_eighty=#{isBeyondEightyQ}


		and pce.IS_LOWER_LIMIT_MONEY=#{isLowerLimitMoneyQ}


		and pce.IDENTIFY_TYPE <6 


		and pce.IDENTIFY_TYPE =6

sql.where:

		AND TB_COUNT.FILTER_COUNT= #{filterNum}




	       	AND  TB_COUNT.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP} AND TB_COUNT.INPUT_TIME  < (#{endTime,jdbcType=TIMESTAMP} +   interval   '1'  day)




	      	AND  TB_COUNT.INPUT_TIME  < (#{endTime,jdbcType=TIMESTAMP} +   interval   '1'  day)




	      	AND  TB_COUNT.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}




			AND TB_COUNT.CASE_NO LIKE CONCAT(CONCAT('%',#{caseNo,jdbcType=VARCHAR}),'%')


		AND TB_COUNT.CASE_NAME LIKE CONCAT(CONCAT('%',#{caseName,jdbcType=VARCHAR}),'%')


		and TB_COUNT.district_code like #{districtId}||'%'

	and (1 = 2 	

			or TB_COUNT.is_descuss=#{isDescuss}


			 or TB_COUNT.chufa_times > #{chufaTimes} 


			or TB_COUNT.is_serious_case=#{isSeriousCase}


			or TB_COUNT.is_beyond_eighty=#{isBeyondEighty}


			or TB_COUNT.IS_LOWER_LIMIT_MONEY=#{isLowerLimitMoney}


			or TB_COUNT.IDENTIFY_TYPE <6 


			or TB_COUNT.IDENTIFY_TYPE =6 
		)

      and TB_COUNT.IS_DESCUSS = #{isDescussQ}


       and TB_COUNT.CHUFA_TIMES > #{chufaTimesQ} 


      and TB_COUNT.is_serious_case=#{isSeriousCaseQ}


      and TB_COUNT.is_beyond_eighty=#{isBeyondEightyQ}


      and TB_COUNT.IS_LOWER_LIMIT_MONEY=#{isLowerLimitMoneyQ}


      and TB_COUNT.IDENTIFY_TYPE <6 


      and TB_COUNT.IDENTIFY_TYPE =6 


            AND  TO_CHAR(TB_COUNT.INPUT_TIME,'yyyy')=${yearCode}


            AND TO_CHAR(TB_COUNT.INPUT_TIME,'Q')=${quarterCode}


            AND TO_CHAR(TB_COUNT.INPUT_TIME,'MM')=${monthCode}

mapper.select:
SELECT D.DISTRICT_CODE REGION_ID,D.DISTRICT_NAME NAME,NVL(T2.CNT,0) CNT,TO_CHAR(ROUND(NVL(T2.RATIO_PER,0),2),'FM990.00') RATIO_PER FROM 
(
SELECT SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2) REGION_SHORT_ID,RPAD(SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2),6,'0') REGION_ID,COUNT(1) CNT,(RATIO_TO_REPORT(COUNT(1)) OVER())*100 RATIO_PER 
FROM CASE_BASIC CB,CASE_STATE CS,ORGANISE O,(SELECT ${regionId} REGION_ID,regexp_replace(${regionId},'(00)+$') SHORT_REGION,LENGTH(regexp_replace(${regionId},'(00)+$')) REGION_LEN FROM DUAL) RDUAL
WHERE CB.CASE_ID=CS.CASE_ID AND CS.CHUFA_STATE=2 AND CB.INPUT_UNIT=O.ORG_CODE

    AND O.INDUSTRY_TYPE=#{industryType}

AND O.DISTRICT_CODE LIKE RDUAL.SHORT_REGION||'%'

    AND  TO_CHAR(CB.INPUT_TIME,'YYYY')=${yearStr}


    AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}


    AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}

GROUP BY SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2) 
) T2,DISTRICT D WHERE D.DISTRICT_CODE=T2.REGION_ID(+)
AND (D.UP_DISTRICT_CODE=${regionId} OR D.DISTRICT_CODE=${regionId})
ORDER BY D.DISTRICT_CODE

SELECT TB_FINAL.REGION_ID,TB_FINAL.NAME,TB_FINAL.CNT, DECODE(TB_FINAL.LAG_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.CNT-TB_FINAL.LAG_CNT)*100/TB_FINAL.LAG_CNT),2),'FM999990.00')) YOY_CNT, TB_FINAL.ZJ_YS_CNT, DECODE(TB_FINAL.LAG_ZJ_YS_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.ZJ_YS_CNT-TB_FINAL.LAG_ZJ_YS_CNT)*100/TB_FINAL.LAG_ZJ_YS_CNT),2),'FM999990.00')) YOY_ZJ_YS_CNT, TB_FINAL.JY_YS_CNT, DECODE(TB_FINAL.LAG_JY_YS_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.JY_YS_CNT-TB_FINAL.LAG_JY_YS_CNT)*100/TB_FINAL.LAG_JY_YS_CNT),2),'FM999990.00')) YOY_JY_YS_CNT, TB_FINAL.ACCEPT_CNT, DECODE(TB_FINAL.LAG_ACCEPT_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.ACCEPT_CNT-TB_FINAL.LAG_ACCEPT_CNT)*100/TB_FINAL.LAG_ACCEPT_CNT),2),'FM999990.00')) YOY_ACCEPT_CNT, TB_FINAL.LIAN_CNT, DECODE(TB_FINAL.LAG_LIAN_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.LIAN_CNT-TB_FINAL.LAG_LIAN_CNT)*100/TB_FINAL.LAG_LIAN_CNT),2),'FM999990.00')) YOY_LIAN_CNT, TB_FINAL.TQDB_CNT, DECODE(TB_FINAL.LAG_TQDB_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.TQDB_CNT-TB_FINAL.LAG_TQDB_CNT)*100/TB_FINAL.LAG_TQDB_CNT),2),'FM999990.00')) YOY_TQDB_CNT, TB_FINAL.QISU_CNT, DECODE(TB_FINAL.LAG_QISU_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.QISU_CNT-TB_FINAL.LAG_QISU_CNT)*100/TB_FINAL.LAG_QISU_CNT),2),'FM999990.00')) YOY_QISU_CNT, TB_FINAL.PANJUE_CNT, DECODE(TB_FINAL.LAG_PANJUE_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.PANJUE_CNT-TB_FINAL.LAG_PANJUE_CNT)*100/TB_FINAL.LAG_PANJUE_CNT),2),'FM999990.00')) YOY_PANJUE_CNT FROM ( SELECT TB_ALL.REGION_ID,TB_ALL.NAME,TB_ALL.YEAR_STR, NVL(TB_COUNT.CNT,0) CNT, NVL(LAG(TB_COUNT.CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_CNT, NVL(TB_COUNT.ZJ_YS_CNT,0) ZJ_YS_CNT, NVL(LAG(TB_COUNT.ZJ_YS_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_ZJ_YS_CNT, NVL(TB_COUNT.JY_YS_CNT,0) JY_YS_CNT, NVL(LAG(TB_COUNT.JY_YS_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_JY_YS_CNT, NVL(TB_COUNT.ACCEPT_CNT,0) ACCEPT_CNT, NVL(LAG(TB_COUNT.ACCEPT_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_ACCEPT_CNT, NVL(TB_COUNT.LIAN_CNT,0) LIAN_CNT, NVL(LAG(TB_COUNT.LIAN_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_LIAN_CNT, NVL(TB_COUNT.TQDB_CNT,0) TQDB_CNT, NVL(LAG(TB_COUNT.TQDB_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_TQDB_CNT, NVL(TB_COUNT.QISU_CNT,0) QISU_CNT, NVL(LAG(TB_COUNT.QISU_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_QISU_CNT, NVL(TB_COUNT.PANJUE_CNT,0) PANJUE_CNT, NVL(LAG(TB_COUNT.PANJUE_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_PANJUE_CNT FROM ( SELECT T_REGION.REGION_ID,T_REGION.NAME,T_YEAR.YEAR_STR FROM (SELECT D.DISTRICT_CODE REGION_ID,D.DISTRICT_NAME NAME FROM DISTRICT D WHERE D.UP_DISTRICT_CODE=${regionId}) T_REGION, (SELECT ${yearStr}-2+LEVEL YEAR_STR FROM DUAL CONNECT BY LEVEL<3) T_YEAR ORDER BY REGION_ID,YEAR_STR ) TB_ALL, ( SELECT RPAD(SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2),6,0) REGION_ID, TO_CHAR(CB.INPUT_TIME,'YYYY') YEAR_STR, SUM(CASE WHEN CB.YISONG_STATE=2 THEN 1 ELSE 0 END) ZJ_YS_CNT, SUM(CASE WHEN CB.YISONG_STATE=3 THEN 1 ELSE 0 END) JY_YS_CNT, SUM(CASE WHEN CB.YISONG_STATE=2 OR CB.YISONG_STATE=3 THEN 1 ELSE 0 END) ACCEPT_CNT, SUM(CASE WHEN CB.LIAN_STATE=3 THEN 1 ELSE 0 END) LIAN_CNT, SUM(CASE WHEN CB.TIQINGDAIBUPERSON_NUM>0 THEN 1 ELSE 0 END) TQDB_CNT, SUM(CASE WHEN CB.TIQINGQISUPERSON_NUM>0 THEN 1 ELSE 0 END) QISU_CNT, SUM(CASE WHEN CB.PANJUE_STATE=2 THEN 1 ELSE 0 END) PANJUE_CNT, COUNT(1) CNT FROM (select C.CASE_ID,C.INPUT_UNIT,C.INPUT_TIME,C.YISONG_STATE,C.LIAN_STATE,C.DAIBU_STATE,C.QISU_STATE,C.PANJUE_STATE, X.TIQINGDAIBUPERSON_NUM,X.TIQINGQISUPERSON_NUM from (SELECT A.CASE_ID,A.INPUT_UNIT,A.INPUT_TIME,CS.YISONG_STATE,CS.LIAN_STATE,CS.DAIBU_STATE,CS.QISU_STATE,CS.PANJUE_STATE FROM CASE_BASIC A,CASE_STATE CS WHERE A.CASE_ID=CS.CASE_ID) c left join (SELECT CASE_ID, NVL(SUM(CASE WHEN (DAIBU_STATE = 2 OR DAIBU_STATE = 3) THEN 1 ELSE 0 END),0) AS TIQINGDAIBUPERSON_NUM, NVL(SUM(CASE WHEN TIQINGQISU_STATE = 2 THEN 1 ELSE 0 END),0) AS TIQINGQISUPERSON_NUM FROM CASE_XIANYIREN GROUP BY CASE_ID) x on c.case_id=x.case_id) CB, ORGANISE O, (SELECT ${regionId} REGION_ID,regexp_replace(${regionId},'(00)+$') SHORT_REGION,LENGTH(regexp_replace(${regionId},'(00)+$')) REGION_LEN FROM DUAL) RDUAL where CB.INPUT_UNIT=O.ORG_CODE AND O.DISTRICT_CODE LIKE RDUAL.SHORT_REGION||'%'
AND O.INDUSTRY_TYPE=#{industryType}
AND O.DISTRICT_CODE LIKE RDUAL.SHORT_REGION||'%'
AND (TO_CHAR(CB.INPUT_TIME,'YYYY')=${yearStr} OR TO_CHAR(CB.INPUT_TIME,'YYYY')=${yearStr}-1)
AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}
AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}
GROUP BY SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2),TO_CHAR(CB.INPUT_TIME,'YYYY') ) TB_COUNT WHERE TB_ALL.REGION_ID=TB_COUNT.REGION_ID(+) AND TB_ALL.YEAR_STR=TB_COUNT.YEAR_STR(+) ORDER BY REGION_ID,YEAR_STR ) TB_FINAL WHERE TB_FINAL.YEAR_STR=${yearStr}

SELECT TB_FINAL.CNT, DECODE(TB_FINAL.LAG_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.CNT-TB_FINAL.LAG_CNT)*100/TB_FINAL.LAG_CNT),2),'FM999990.00')) YOY_CNT, TB_FINAL.ZJ_YS_CNT, DECODE(TB_FINAL.LAG_ZJ_YS_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.ZJ_YS_CNT-TB_FINAL.LAG_ZJ_YS_CNT)*100/TB_FINAL.LAG_ZJ_YS_CNT),2),'FM999990.00')) YOY_ZJ_YS_CNT, TB_FINAL.JY_YS_CNT, DECODE(TB_FINAL.LAG_JY_YS_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.JY_YS_CNT-TB_FINAL.LAG_JY_YS_CNT)*100/TB_FINAL.LAG_JY_YS_CNT),2),'FM999990.00')) YOY_JY_YS_CNT, TB_FINAL.ACCEPT_CNT, DECODE(TB_FINAL.LAG_ACCEPT_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.ACCEPT_CNT-TB_FINAL.LAG_ACCEPT_CNT)*100/TB_FINAL.LAG_ACCEPT_CNT),2),'FM999990.00')) YOY_ACCEPT_CNT, TB_FINAL.LIAN_CNT, DECODE(TB_FINAL.LAG_LIAN_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.LIAN_CNT-TB_FINAL.LAG_LIAN_CNT)*100/TB_FINAL.LAG_LIAN_CNT),2),'FM999990.00')) YOY_LIAN_CNT, TB_FINAL.TQDB_CNT, DECODE(TB_FINAL.LAG_TQDB_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.TQDB_CNT-TB_FINAL.LAG_TQDB_CNT)*100/TB_FINAL.LAG_TQDB_CNT),2),'FM999990.00')) YOY_TQDB_CNT, TB_FINAL.QISU_CNT, DECODE(TB_FINAL.LAG_QISU_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.QISU_CNT-TB_FINAL.LAG_QISU_CNT)*100/TB_FINAL.LAG_QISU_CNT),2),'FM999990.00')) YOY_QISU_CNT, TB_FINAL.PANJUE_CNT, DECODE(TB_FINAL.LAG_PANJUE_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.PANJUE_CNT-TB_FINAL.LAG_PANJUE_CNT)*100/TB_FINAL.LAG_PANJUE_CNT),2),'FM999990.00')) YOY_PANJUE_CNT FROM ( SELECT TB_COUNT.year_str, NVL(TB_COUNT.CNT,0) CNT, NVL(LAG(TB_COUNT.CNT,1) OVER(ORDER BY TB_COUNT.YEAR_STR),0) LAG_CNT, NVL(TB_COUNT.ZJ_YS_CNT,0) ZJ_YS_CNT, NVL(LAG(TB_COUNT.ZJ_YS_CNT,1) OVER(ORDER BY TB_COUNT.YEAR_STR),0) LAG_ZJ_YS_CNT, NVL(TB_COUNT.JY_YS_CNT,0) JY_YS_CNT, NVL(LAG(TB_COUNT.JY_YS_CNT,1) OVER(ORDER BY TB_COUNT.YEAR_STR),0) LAG_JY_YS_CNT, NVL(TB_COUNT.ACCEPT_CNT,0) ACCEPT_CNT, NVL(LAG(TB_COUNT.ACCEPT_CNT,1) OVER(ORDER BY TB_COUNT.YEAR_STR),0) LAG_ACCEPT_CNT, NVL(TB_COUNT.LIAN_CNT,0) LIAN_CNT, NVL(LAG(TB_COUNT.LIAN_CNT,1) OVER(ORDER BY TB_COUNT.YEAR_STR),0) LAG_LIAN_CNT, NVL(TB_COUNT.TQDB_CNT,0) TQDB_CNT, NVL(LAG(TB_COUNT.TQDB_CNT,1) OVER(ORDER BY TB_COUNT.YEAR_STR),0) LAG_TQDB_CNT, NVL(TB_COUNT.QISU_CNT,0) QISU_CNT, NVL(LAG(TB_COUNT.QISU_CNT,1) OVER(ORDER BY TB_COUNT.YEAR_STR),0) LAG_QISU_CNT, NVL(TB_COUNT.PANJUE_CNT,0) PANJUE_CNT, NVL(LAG(TB_COUNT.PANJUE_CNT,1) OVER(ORDER BY TB_COUNT.YEAR_STR),0) LAG_PANJUE_CNT FROM ( SELECT TO_CHAR(CB.INPUT_TIME,'YYYY') YEAR_STR, SUM(CASE WHEN CB.YISONG_STATE=2 THEN 1 ELSE 0 END) ZJ_YS_CNT, SUM(CASE WHEN CB.YISONG_STATE=3 THEN 1 ELSE 0 END) JY_YS_CNT, SUM(CASE WHEN CB.YISONG_STATE=2 OR CB.YISONG_STATE=3 THEN 1 ELSE 0 END) ACCEPT_CNT, SUM(CASE WHEN CB.LIAN_STATE=3 THEN 1 ELSE 0 END) LIAN_CNT, SUM(CASE WHEN CB.TIQINGDAIBUPERSON_NUM>0 THEN 1 ELSE 0 END) TQDB_CNT, SUM(CASE WHEN CB.TIQINGQISUPERSON_NUM>0 THEN 1 ELSE 0 END) QISU_CNT, SUM(CASE WHEN CB.PANJUE_STATE=2 THEN 1 ELSE 0 END) PANJUE_CNT, COUNT(1) CNT FROM (select C.CASE_ID,C.INPUT_UNIT,C.INPUT_TIME,C.YISONG_STATE,C.LIAN_STATE,C.DAIBU_STATE,C.QISU_STATE,C.PANJUE_STATE, X.TIQINGDAIBUPERSON_NUM,X.TIQINGQISUPERSON_NUM from (SELECT A.CASE_ID,A.INPUT_UNIT,A.INPUT_TIME,CS.YISONG_STATE,CS.LIAN_STATE,CS.DAIBU_STATE,CS.QISU_STATE,CS.PANJUE_STATE FROM CASE_BASIC A,CASE_STATE CS WHERE A.CASE_ID=CS.CASE_ID) c left join (SELECT CASE_ID, NVL(SUM(CASE WHEN (DAIBU_STATE = 2 OR DAIBU_STATE = 3) THEN 1 ELSE 0 END),0) AS TIQINGDAIBUPERSON_NUM, NVL(SUM(CASE WHEN TIQINGQISU_STATE = 2 THEN 1 ELSE 0 END),0) AS TIQINGQISUPERSON_NUM FROM CASE_XIANYIREN GROUP BY CASE_ID) x on c.case_id=x.case_id) CB, ORGANISE O where CB.INPUT_UNIT=O.ORG_CODE
AND O.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'
AND (TO_CHAR(CB.input_time,'yyyy')=${yearStr}-1 OR TO_CHAR(CB.input_time,'yyyy')=${yearStr})
AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}
AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}
AND O.INDUSTRY_TYPE = #{industryType}
GROUP BY TO_CHAR(CB.INPUT_TIME,'YYYY') ) TB_COUNT ORDER BY YEAR_STR ) TB_FINAL WHERE TB_FINAL.YEAR_STR=${yearStr}

SELECT D.DISTRICT_CODE REGION_ID,D.DISTRICT_NAME NAME,NVL(T2.CNT,0) CNT,TO_CHAR(ROUND(NVL(T2.RATIO_PER,0),2),'FM990.00') RATIO_PER FROM 
(
SELECT SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2) REGION_SHORT_ID,RPAD(SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2),6,'0') REGION_ID,COUNT(1) CNT,(RATIO_TO_REPORT(COUNT(1)) OVER())*100 RATIO_PER 
FROM CASE_BASIC CB,CASE_STATE CS,ORGANISE O,(SELECT ${regionId} REGION_ID,regexp_replace(${regionId},'(00)+$') SHORT_REGION,LENGTH(regexp_replace(${regionId},'(00)+$')) REGION_LEN FROM DUAL) RDUAL
WHERE CS.CASE_ID=CB.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE AND CS.CHUFA_STATE=2 AND O.ORG_TYPE IN (1,3)
AND O.DISTRICT_CODE LIKE RDUAL.SHORT_REGION||'%'

    AND  TO_CHAR(CB.INPUT_TIME,'YYYY')=${yearStr} 


    AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}


    AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}

GROUP BY SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2) 
) T2,DISTRICT D WHERE D.DISTRICT_CODE=T2.REGION_ID(+)
AND (D.UP_DISTRICT_CODE=${regionId} OR D.DISTRICT_CODE=${regionId})
ORDER BY D.DISTRICT_CODE

SELECT TB_ALL.INDUSTRY_ID,TB_ALL.INDUSTRY_NAME,NVL(TB_CNT.CNT,0) CNT,NVL(TB_CNT.RATIO_PER,'0.00') RATIO_PER FROM
(
  SELECT O.INDUSTRY_TYPE INDUSTRY_ID,O.INDUSTRY_NAME FROM ORGANISE O WHERE O.INDUSTRY_NAME IS NOT NULL GROUP BY O.INDUSTRY_TYPE,O.INDUSTRY_NAME
) TB_ALL,
(
	SELECT O.INDUSTRY_TYPE INDUSTRY_ID,O.INDUSTRY_NAME,COUNT(1) CNT,TO_CHAR(ROUND(((RATIO_TO_REPORT(COUNT(1)) OVER())*100),2),'FM990.00') RATIO_PER  
	FROM CASE_BASIC CB,CASE_STATE CS,ORGANISE O,(SELECT ${regionId} REGION_ID,regexp_replace(${regionId},'(00)+$') SHORT_REGION,LENGTH(regexp_replace(${regionId},'(00)+$')) REGION_LEN FROM DUAL) RDUAL
	WHERE CB.CASE_ID=CS.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE AND CS.CHUFA_STATE=2 AND O.INDUSTRY_NAME IS NOT NULL
	AND O.DISTRICT_CODE LIKE RDUAL.SHORT_REGION||'%'

			AND  TO_CHAR(CB.INPUT_TIME,'YYYY')=${yearStr} 


			AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}


			AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}

	GROUP BY O.INDUSTRY_TYPE,O.INDUSTRY_NAME ORDER BY O.INDUSTRY_TYPE
) TB_CNT
WHERE TB_ALL.INDUSTRY_ID=TB_CNT.INDUSTRY_ID(+) AND TB_ALL.INDUSTRY_NAME=TB_CNT.INDUSTRY_NAME(+)
ORDER BY TB_ALL.INDUSTRY_ID

SELECT TB_FINAL.REGION_ID,TB_FINAL.NAME,TB_FINAL.CNT, DECODE(TB_FINAL.LAG_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.CNT-TB_FINAL.LAG_CNT)*100/TB_FINAL.LAG_CNT),2),'FM999990.00')) YOY_CNT, TB_FINAL.ZJ_YS_CNT, DECODE(TB_FINAL.LAG_ZJ_YS_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.ZJ_YS_CNT-TB_FINAL.LAG_ZJ_YS_CNT)*100/TB_FINAL.LAG_ZJ_YS_CNT),2),'FM999990.00')) YOY_ZJ_YS_CNT, TB_FINAL.JY_YS_CNT, DECODE(TB_FINAL.LAG_JY_YS_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.JY_YS_CNT-TB_FINAL.LAG_JY_YS_CNT)*100/TB_FINAL.LAG_JY_YS_CNT),2),'FM999990.00')) YOY_JY_YS_CNT, TB_FINAL.ACCEPT_CNT, DECODE(TB_FINAL.LAG_ACCEPT_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.ACCEPT_CNT-TB_FINAL.LAG_ACCEPT_CNT)*100/TB_FINAL.LAG_ACCEPT_CNT),2),'FM999990.00')) YOY_ACCEPT_CNT, TB_FINAL.LIAN_CNT, DECODE(TB_FINAL.LAG_LIAN_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.LIAN_CNT-TB_FINAL.LAG_LIAN_CNT)*100/TB_FINAL.LAG_LIAN_CNT),2),'FM999990.00')) YOY_LIAN_CNT, TB_FINAL.TQDB_CNT, DECODE(TB_FINAL.LAG_TQDB_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.TQDB_CNT-TB_FINAL.LAG_TQDB_CNT)*100/TB_FINAL.LAG_TQDB_CNT),2),'FM999990.00')) YOY_TQDB_CNT, TB_FINAL.QISU_CNT, DECODE(TB_FINAL.LAG_QISU_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.QISU_CNT-TB_FINAL.LAG_QISU_CNT)*100/TB_FINAL.LAG_QISU_CNT),2),'FM999990.00')) YOY_QISU_CNT, TB_FINAL.PANJUE_CNT, DECODE(TB_FINAL.LAG_PANJUE_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.PANJUE_CNT-TB_FINAL.LAG_PANJUE_CNT)*100/TB_FINAL.LAG_PANJUE_CNT),2),'FM999990.00')) YOY_PANJUE_CNT FROM ( SELECT TB_ALL.REGION_ID,TB_ALL.NAME,TB_ALL.YEAR_STR, NVL(TB_COUNT.CNT,0) CNT, NVL(LAG(TB_COUNT.CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_CNT, NVL(TB_COUNT.ZJ_YS_CNT,0) ZJ_YS_CNT, NVL(LAG(TB_COUNT.ZJ_YS_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_ZJ_YS_CNT, NVL(TB_COUNT.JY_YS_CNT,0) JY_YS_CNT, NVL(LAG(TB_COUNT.JY_YS_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_JY_YS_CNT, NVL(TB_COUNT.ACCEPT_CNT,0) ACCEPT_CNT, NVL(LAG(TB_COUNT.ACCEPT_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_ACCEPT_CNT, NVL(TB_COUNT.LIAN_CNT,0) LIAN_CNT, NVL(LAG(TB_COUNT.LIAN_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_LIAN_CNT, NVL(TB_COUNT.TQDB_CNT,0) TQDB_CNT, NVL(LAG(TB_COUNT.TQDB_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_TQDB_CNT, NVL(TB_COUNT.QISU_CNT,0) QISU_CNT, NVL(LAG(TB_COUNT.QISU_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_QISU_CNT, NVL(TB_COUNT.PANJUE_CNT,0) PANJUE_CNT, NVL(LAG(TB_COUNT.PANJUE_CNT,1) OVER(ORDER BY TB_ALL.REGION_ID,TB_ALL.YEAR_STR),0) LAG_PANJUE_CNT FROM ( SELECT T_REGION.REGION_ID,T_REGION.NAME,T_YEAR.YEAR_STR FROM (SELECT R.DISTRICT_CODE REGION_ID,R.DISTRICT_NAME NAME FROM DISTRICT R WHERE R.UP_DISTRICT_CODE=${regionId} or R.DISTRICT_CODE=${regionId}) T_REGION, (SELECT ${yearStr}-2+LEVEL YEAR_STR FROM DUAL CONNECT BY LEVEL<3) T_YEAR ORDER BY REGION_ID,YEAR_STR ) TB_ALL, ( SELECT RPAD(SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2),6,0) REGION_ID, TO_CHAR(CB.INPUT_TIME,'YYYY') YEAR_STR, SUM(CASE WHEN CB.YISONG_STATE=2 THEN 1 ELSE 0 END) ZJ_YS_CNT, SUM(CASE WHEN CB.YISONG_STATE=3 THEN 1 ELSE 0 END) JY_YS_CNT, SUM(CASE WHEN CB.YISONG_STATE=2 OR CB.YISONG_STATE=3 THEN 1 ELSE 0 END) ACCEPT_CNT, SUM(CASE WHEN CB.LIAN_STATE=3 THEN 1 ELSE 0 END) LIAN_CNT, SUM(CASE WHEN CB.TIQINGDAIBUPERSON_NUM>0 THEN 1 ELSE 0 END) TQDB_CNT, SUM(CASE WHEN CB.TIQINGQISUPERSON_NUM>0 THEN 1 ELSE 0 END) QISU_CNT, SUM(CASE WHEN CB.PANJUE_STATE=2 THEN 1 ELSE 0 END) PANJUE_CNT, COUNT(1) CNT FROM (select C.CASE_ID,C.INPUT_UNIT,C.INPUT_TIME,C.YISONG_STATE,C.LIAN_STATE,C.DAIBU_STATE,C.QISU_STATE,C.PANJUE_STATE, X.TIQINGDAIBUPERSON_NUM,X.TIQINGQISUPERSON_NUM from (SELECT A.CASE_ID,A.INPUT_UNIT,A.INPUT_TIME,CS.YISONG_STATE,CS.LIAN_STATE,CS.DAIBU_STATE,CS.QISU_STATE,CS.PANJUE_STATE FROM CASE_BASIC A,CASE_STATE CS WHERE A.CASE_ID=CS.CASE_ID) c left join (SELECT CASE_ID, NVL(SUM(CASE WHEN (DAIBU_STATE = 2 OR DAIBU_STATE = 3) THEN 1 ELSE 0 END),0) AS TIQINGDAIBUPERSON_NUM, NVL(SUM(CASE WHEN TIQINGQISU_STATE = 2 THEN 1 ELSE 0 END),0) AS TIQINGQISUPERSON_NUM FROM CASE_XIANYIREN GROUP BY CASE_ID) x on c.case_id=x.case_id) CB, ORGANISE O, (SELECT ${regionId} REGION_ID,regexp_replace(${regionId},'(00)+$') SHORT_REGION,LENGTH(regexp_replace(${regionId},'(00)+$')) REGION_LEN FROM DUAL) RDUAL where CB.INPUT_UNIT=O.ORG_CODE AND O.DISTRICT_CODE LIKE RDUAL.SHORT_REGION||'%'
AND (TO_CHAR(CB.INPUT_TIME,'yyyy')=${yearStr}-1 OR TO_CHAR(CB.INPUT_TIME,'yyyy')=${yearStr})
AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}
AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}
GROUP BY SUBSTR(O.DISTRICT_CODE,1,RDUAL.REGION_LEN+2),TO_CHAR(CB.INPUT_TIME,'YYYY') ) TB_COUNT WHERE TB_ALL.REGION_ID=TB_COUNT.REGION_ID(+) AND TB_ALL.YEAR_STR=TB_COUNT.YEAR_STR(+) ORDER BY REGION_ID,YEAR_STR ) TB_FINAL WHERE TB_FINAL.YEAR_STR=${yearStr}

SELECT TB_FINAL.INDUSTRY_ID,TB_FINAL.INDUSTRY_NAME,TB_FINAL.CNT, DECODE(TB_FINAL.LAG_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.CNT-TB_FINAL.LAG_CNT)*100/TB_FINAL.LAG_CNT),2),'FM999990.00')) YOY_CNT, TB_FINAL.ZJ_YS_CNT, DECODE(TB_FINAL.LAG_ZJ_YS_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.ZJ_YS_CNT-TB_FINAL.LAG_ZJ_YS_CNT)*100/TB_FINAL.LAG_ZJ_YS_CNT),2),'FM999990.00')) YOY_ZJ_YS_CNT, TB_FINAL.JY_YS_CNT, DECODE(TB_FINAL.LAG_JY_YS_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.JY_YS_CNT-TB_FINAL.LAG_JY_YS_CNT)*100/TB_FINAL.LAG_JY_YS_CNT),2),'FM999990.00')) YOY_JY_YS_CNT, TB_FINAL.ACCEPT_CNT, DECODE(TB_FINAL.LAG_ACCEPT_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.ACCEPT_CNT-TB_FINAL.LAG_ACCEPT_CNT)*100/TB_FINAL.LAG_ACCEPT_CNT),2),'FM999990.00')) YOY_ACCEPT_CNT, TB_FINAL.LIAN_CNT, DECODE(TB_FINAL.LAG_LIAN_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.LIAN_CNT-TB_FINAL.LAG_LIAN_CNT)*100/TB_FINAL.LAG_LIAN_CNT),2),'FM999990.00')) YOY_LIAN_CNT, TB_FINAL.TQDB_CNT, DECODE(TB_FINAL.LAG_TQDB_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.TQDB_CNT-TB_FINAL.LAG_TQDB_CNT)*100/TB_FINAL.LAG_TQDB_CNT),2),'FM999990.00')) YOY_TQDB_CNT, TB_FINAL.QISU_CNT, DECODE(TB_FINAL.LAG_QISU_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.QISU_CNT-TB_FINAL.LAG_QISU_CNT)*100/TB_FINAL.LAG_QISU_CNT),2),'FM999990.00')) YOY_QISU_CNT, TB_FINAL.PANJUE_CNT, DECODE(TB_FINAL.LAG_PANJUE_CNT,0,'---',TO_CHAR(ROUND(((TB_FINAL.PANJUE_CNT-TB_FINAL.LAG_PANJUE_CNT)*100/TB_FINAL.LAG_PANJUE_CNT),2),'FM999990.00')) YOY_PANJUE_CNT FROM ( SELECT TB_ALL.INDUSTRY_ID,TB_ALL.INDUSTRY_NAME,TB_ALL.YEAR_STR, NVL(TB_COUNT.CNT,0) CNT, NVL(LAG(TB_COUNT.CNT,1) OVER(ORDER BY TB_COUNT.INDUSTRY_ID,TB_COUNT.INDUSTRY_NAME,TB_ALL.YEAR_STR),0) LAG_CNT, NVL(TB_COUNT.ZJ_YS_CNT,0) ZJ_YS_CNT, NVL(LAG(TB_COUNT.ZJ_YS_CNT,1) OVER(ORDER BY TB_COUNT.INDUSTRY_ID,TB_COUNT.INDUSTRY_NAME,TB_ALL.YEAR_STR),0) LAG_ZJ_YS_CNT, NVL(TB_COUNT.JY_YS_CNT,0) JY_YS_CNT, NVL(LAG(TB_COUNT.JY_YS_CNT,1) OVER(ORDER BY TB_COUNT.INDUSTRY_ID,TB_COUNT.INDUSTRY_NAME,TB_ALL.YEAR_STR),0) LAG_JY_YS_CNT, NVL(TB_COUNT.ACCEPT_CNT,0) ACCEPT_CNT, NVL(LAG(TB_COUNT.ACCEPT_CNT,1) OVER(ORDER BY TB_COUNT.INDUSTRY_ID,TB_COUNT.INDUSTRY_NAME,TB_ALL.YEAR_STR),0) LAG_ACCEPT_CNT, NVL(TB_COUNT.LIAN_CNT,0) LIAN_CNT, NVL(LAG(TB_COUNT.LIAN_CNT,1) OVER(ORDER BY TB_COUNT.INDUSTRY_ID,TB_COUNT.INDUSTRY_NAME,TB_ALL.YEAR_STR),0) LAG_LIAN_CNT, NVL(TB_COUNT.TQDB_CNT,0) TQDB_CNT, NVL(LAG(TB_COUNT.TQDB_CNT,1) OVER(ORDER BY TB_COUNT.INDUSTRY_ID,TB_COUNT.INDUSTRY_NAME,TB_ALL.YEAR_STR),0) LAG_TQDB_CNT, NVL(TB_COUNT.QISU_CNT,0) QISU_CNT, NVL(LAG(TB_COUNT.QISU_CNT,1) OVER(ORDER BY TB_COUNT.INDUSTRY_ID,TB_COUNT.INDUSTRY_NAME,TB_ALL.YEAR_STR),0) LAG_QISU_CNT, NVL(TB_COUNT.PANJUE_CNT,0) PANJUE_CNT, NVL(LAG(TB_COUNT.PANJUE_CNT,1) OVER(ORDER BY TB_COUNT.INDUSTRY_ID,TB_COUNT.INDUSTRY_NAME,TB_ALL.YEAR_STR),0) LAG_PANJUE_CNT FROM ( SELECT INDUSTRY_ID,INDUSTRY_NAME,YEAR_STR FROM (SELECT DISTINCT PO1.INDUSTRY_TYPE INDUSTRY_ID,PO1.INDUSTRY_NAME FROM ORGANISE PO1 WHERE EXISTS ( SELECT 1 FROM CASE_BASIC C1 WHERE C1.INPUT_UNIT=PO1.ORG_CODE
AND PO1.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'
AND (TO_CHAR(C1.INPUT_TIME,'yyyy')=${yearStr}-1 OR TO_CHAR(C1.INPUT_TIME,'yyyy')=${yearStr})
AND TO_CHAR(C1.INPUT_TIME,'Q')=${quarterStr}
AND TO_CHAR(C1.INPUT_TIME,'MM')=${monthStr}
) ) TB_INDUSTRY, (SELECT ${yearStr}-2+LEVEL YEAR_STR FROM DUAL CONNECT BY LEVEL<3) TB_YEAR ORDER BY INDUSTRY_ID,INDUSTRY_NAME,YEAR_STR ) TB_ALL, ( SELECT O.INDUSTRY_TYPE INDUSTRY_ID,O.INDUSTRY_NAME, TO_CHAR(CB.INPUT_TIME,'YYYY') YEAR_STR, SUM(CASE WHEN CB.YISONG_STATE=2 THEN 1 ELSE 0 END) ZJ_YS_CNT, SUM(CASE WHEN CB.YISONG_STATE=3 THEN 1 ELSE 0 END) JY_YS_CNT, SUM(CASE WHEN CB.YISONG_STATE=2 OR CB.YISONG_STATE=3 THEN 1 ELSE 0 END) ACCEPT_CNT, SUM(CASE WHEN CB.LIAN_STATE=3 THEN 1 ELSE 0 END) LIAN_CNT, SUM(CASE WHEN CB.TIQINGDAIBUPERSON_NUM>0 THEN 1 ELSE 0 END) TQDB_CNT, SUM(CASE WHEN CB.TIQINGQISUPERSON_NUM>0 THEN 1 ELSE 0 END) QISU_CNT, SUM(CASE WHEN CB.PANJUE_STATE=2 THEN 1 ELSE 0 END) PANJUE_CNT, COUNT(1) CNT FROM (select C.CASE_ID,C.INPUT_UNIT,C.INPUT_TIME,C.YISONG_STATE,C.LIAN_STATE,C.DAIBU_STATE,C.QISU_STATE,C.PANJUE_STATE, X.TIQINGDAIBUPERSON_NUM,X.TIQINGQISUPERSON_NUM from (SELECT A.CASE_ID,A.INPUT_UNIT,A.INPUT_TIME,CS.YISONG_STATE,CS.LIAN_STATE,CS.DAIBU_STATE,CS.QISU_STATE,CS.PANJUE_STATE FROM CASE_BASIC A,CASE_STATE CS WHERE A.CASE_ID=CS.CASE_ID) c left join (SELECT CASE_ID, NVL(SUM(CASE WHEN (DAIBU_STATE = 2 OR DAIBU_STATE = 3) THEN 1 ELSE 0 END),0) AS TIQINGDAIBUPERSON_NUM, NVL(SUM(CASE WHEN TIQINGQISU_STATE = 2 THEN 1 ELSE 0 END),0) AS TIQINGQISUPERSON_NUM FROM CASE_XIANYIREN GROUP BY CASE_ID) x on c.case_id=x.case_id) CB, ORGANISE O where CB.INPUT_UNIT=O.ORG_CODE
AND O.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'
AND (TO_CHAR(CB.INPUT_TIME,'yyyy')=${yearStr}-1 OR TO_CHAR(CB.INPUT_TIME,'yyyy')=${yearStr})
AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}
AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}
GROUP BY O.INDUSTRY_TYPE,O.INDUSTRY_NAME,TO_CHAR(CB.INPUT_TIME,'YYYY') ) TB_COUNT WHERE TB_ALL.INDUSTRY_ID=TB_COUNT.INDUSTRY_ID(+) AND TB_ALL.INDUSTRY_NAME=TB_COUNT.INDUSTRY_NAME(+) AND TB_ALL.YEAR_STR=TB_COUNT.YEAR_STR(+) ORDER BY TB_ALL.INDUSTRY_ID,TB_ALL.INDUSTRY_NAME,TB_ALL.YEAR_STR ) TB_FINAL WHERE TB_FINAL.YEAR_STR=${yearStr} AND TB_FINAL.INDUSTRY_ID IS NOT NULL

SELECT * FROM ( SELECT COUNT(AI.NAME) ACCUSE_NUM, AI.ID ACCUSE_ID, AI.NAME ACCUSE_NAME FROM CASE_BASIC CB,ORGANISE O, (SELECT CASE_ID, ACCUSE_ID FROM CASE_ACCUSE A UNION SELECT B.CASE_ID, ACCUSE_ID FROM XIANYIREN_ACCUSE B GROUP BY ACCUSE_ID, CASE_ID ) CA, ACCUSE_INFO AI WHERE CB.CASE_ID = CA.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE AND AI.ID = CA.ACCUSE_ID AND accuse_id != 190
AND O.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'
AND TO_CHAR(CB.INPUT_TIME,'yyyy')=${yearStr}
AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}
AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}
GROUP BY AI.ID,AI.NAME ORDER BY ACCUSE_NUM DESC )

select count(1) FROM CASE_BASIC CB,CASE_STATE CS,ORGANISE O
where CB.CASE_ID=CS.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE

		AND O.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'


		AND  TO_CHAR(CB.INPUT_TIME,'yyyy')=${yearStr}


		AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}


		AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}


		AND CS.CHUFA_STATE = ${chufaState}

SELECT COUNT(distinct cb.case_id) FROM CASE_BASIC CB,CASE_STATE CS,ORGANISE O, ( SELECT CASE_ID,LA.NAME AS ACCUSE_NAME FROM CASE_ACCUSE_RULE_RELATION CI,ACCUSE_RULE I, ACCUSE_RULE_RELATION SA,ACCUSE_INFO LA WHERE CI.RULE_ID = I.ID AND CI.RULE_ID = SA.RULE_ID AND SA.ACCUSE_ID = LA.ID AND SA.ACCUSE_ID!=190) T WHERE CB.CASE_ID = T.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE AND CB.CASE_STATE < 4 AND CB.CASE_ID = CS.CASE_ID AND CB.IS_SUSPECTED_CRIMINAL = 1
AND O.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'
AND TO_CHAR(CB.INPUT_TIME,'yyyy')=${yearStr}
AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}
AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}
AND CS.CHUFA_STATE = ${chufaState}

SELECT nvl(SUM(CASE WHEN FILTER_COUNT > 0 THEN 1 ELSE 0 END),0) AS JGCL_TOTAL, nvl(SUM(CASE WHEN FILTER_COUNT = 6 THEN 1 ELSE 0 END),0) AS JGCL_FILTER_1, nvl(SUM(CASE WHEN FILTER_COUNT = 5 THEN 1 ELSE 0 END),0) AS JGCL_FILTER_2, nvl(SUM(CASE WHEN FILTER_COUNT = 4 THEN 1 ELSE 0 END),0) AS JGCL_FILTER_3, nvl(SUM(CASE WHEN FILTER_COUNT = 3 THEN 1 ELSE 0 END),0) AS JGCL_FILTER_4, nvl(SUM(CASE WHEN FILTER_COUNT = 2 THEN 1 ELSE 0 END),0) AS JGCL_FILTER_5, nvl(SUM(CASE WHEN FILTER6_COUNT = 3 THEN 1 ELSE 0 END),0) AS JGCL_FILTER_6, nvl(SUM(CASE WHEN FILTER7_COUNT = 3 THEN 1 ELSE 0 END),0) AS JGCL_FILTER_7, nvl(SUM(CASE WHEN FILTER8_COUNT = 4 THEN 1 ELSE 0 END),0) AS JGCL_FILTER_8 FROM ( SELECT (TB_FILTERS.LOWER_MONEY_FILTER+TB_FILTERS.IDENTIFY_FILTER+TB_FILTERS.DESCUSS_FILTER+TB_FILTERS.BEYOND_FILTER+TB_FILTERS.SERIOUS_FILTER+TB_FILTERS.TIMES_FILTER) FILTER_COUNT, (TB_FILTERS.BEYOND_FILTER+TB_FILTERS.DESCUSS_FILTER+TB_FILTERS.IDENTIFY_FILTER) AS FILTER6_COUNT, (TB_FILTERS.TIMES_FILTER+TB_FILTERS.DESCUSS_FILTER+TB_FILTERS.IDENTIFY_FILTER) AS FILTER7_COUNT, (TB_FILTERS.SERIOUS_FILTER+TB_FILTERS.BEYOND_FILTER+TB_FILTERS.DESCUSS_FILTER+TB_FILTERS.IDENTIFY_FILTER) AS FILTER8_COUNT FROM ( SELECT CASE WHEN PCE.IS_LOWER_LIMIT_MONEY=1 THEN 1 ELSE 0 END LOWER_MONEY_FILTER, CASE WHEN PCE.IS_IDENTIFY < 6 THEN 1 ELSE 0 END IDENTIFY_FILTER, CASE WHEN PCE.IS_DESCUSS=1 THEN 1 ELSE 0 END DESCUSS_FILTER, CASE WHEN PCE.IS_BEYOND_EIGHTY=1 THEN 1 ELSE 0 END BEYOND_FILTER, CASE WHEN PCE.IS_SERIOUS_CASE=1 THEN 1 ELSE 0 END SERIOUS_FILTER, CASE WHEN PCE.CHUFA_TIMES >1 THEN 1 ELSE 0 END TIMES_FILTER FROM CASE_BASIC CB,CASE_STATE CS,ORGANISE O,PENALTY_CASE_FORM PCE WHERE CB.CASE_ID=CS.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE AND CB.INPUT_UNIT=O.ORG_CODE AND CB.CASE_ID=PCE.CASE_ID(+) AND CB.CASE_STATE = 2 AND CS.CHUFA_STATE = 2
AND O.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'
AND TO_CHAR(CB.INPUT_TIME,'yyyy')=${yearStr}
AND TO_CHAR(CB.INPUT_TIME,'Q')=${quarterStr}
AND TO_CHAR(CB.INPUT_TIME,'MM')=${monthStr}
AND CS.CHUFA_STATE = ${chufaState}
) TB_FILTERS ) TB_COUNT

SELECT COUNT(DISTINCT CB1.CASE_ID) FROM (SELECT CB.CASE_ID,CB.CASE_NO,CB.CASE_NAME,CB.INPUTER,CB.INPUT_TIME,PCE.CHUFA_TIME,CB.ANFA_ADDRESS,CB.ANFA_TIME, CB.AMOUNT_INVOLVED,O.ORG_CODE,O.ORG_NAME,PCE.identify_type, CB.CASE_STATE,CB.RECORD_SRC,CB.LATEST_POCESS_TIME,CB.PROC_KEY,CST.CHUFA_STATE,PCE.GOODS_INVOLVED, CX.IDS_NO,CX."NAME" AS XIANYIREN_NAME,CC."NAME" AS COMPANY_NAME,O.DISTRICT_CODE FROM CASE_BASIC CB,CASE_STATE CST,PENALTY_CASE_FORM PCE,CASE_XIANYIREN CX,CASE_COMPANY CC,USER_ U,ORGANISE O WHERE CB.PROC_KEY = 'chufaProc' AND CB.CASE_ID = CST.CASE_ID AND CB.CASE_ID =PCE.CASE_ID(+) AND CB.CASE_ID = CX.CASE_ID(+) AND CB.CASE_ID= CC.CASE_ID(+) AND CB.CASE_TYPE IN (1,2) AND CB.INPUTER = U.USER_ID AND O.ORG_CODE=CB.INPUT_UNIT
AND O.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'
AND CST.CHUFA_STATE = ${chufaState}
) CB1, (SELECT CB.CASE_ID,CB.CASE_NO,CB.CASE_NAME,CB.INPUTER,CB.INPUT_TIME,PCE.CHUFA_TIME,CB.ANFA_ADDRESS,CB.ANFA_TIME, CB.AMOUNT_INVOLVED,O.ORG_CODE,O.ORG_NAME,PCE.identify_type, CB.CASE_STATE,CB.RECORD_SRC,CB.LATEST_POCESS_TIME,CB.PROC_KEY,CST.CHUFA_STATE,PCE.GOODS_INVOLVED, CX.IDS_NO,CX."NAME" AS XIANYIREN_NAME,CC."NAME" AS COMPANY_NAME,O.DISTRICT_CODE FROM CASE_BASIC CB,CASE_STATE CST,PENALTY_CASE_FORM PCE,CASE_XIANYIREN CX,CASE_COMPANY CC,USER_ U,ORGANISE O WHERE CB.PROC_KEY = 'chufaProc' AND CB.CASE_ID = CST.CASE_ID AND CB.CASE_ID =PCE.CASE_ID(+) AND CB.CASE_ID = CX.CASE_ID(+) AND CB.CASE_ID= CC.CASE_ID(+) AND CB.CASE_TYPE IN (1,2) AND CB.INPUTER = U.USER_ID AND O.ORG_CODE=CB.INPUT_UNIT
AND O.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'
AND CST.CHUFA_STATE = ${chufaState}
) CB2 WHERE CB1.CASE_ID != CB2.CASE_ID and CB1.INPUTER = CB2.INPUTER

SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.CASE_STATE,C.INPUT_TIME,U.USER_NAME AS USERNAME,O.DISTRICT_CODE AS DISTRICTCODE,O.ORG_NAME AS ORGNAME, CC.YISONG_TIME FROM CASE_BASIC C,CRIME_CASE_FORM CC,CASE_STATE S, USER_ U,ORGANISE O WHERE C.CASE_ID=S.CASE_ID AND C.CASE_ID=CC.CASE_ID AND C.INPUTER= U.USER_ID AND U.ORG_ID=O.ORG_CODE AND C.CASE_STATE = 10 AND C.PROC_KEY ='chufaProc'
AND ${conditionFormula}
AND O.DISTRICT_CODE LIKE #{shortDistrictId}||'%'
AND C.CASE_NO LIKE CONCAT(CONCAT('%',#{caseNo}),'%')
AND C.CASE_NAME LIKE CONCAT(CONCAT('%',#{caseName}),'%')
AND O.ORG_PATH LIKE CONCAT(#{orgPath},'%')
AND O.ORG_CODE = #{orgId}
AND CC.ACCEPT_ORG = #{acceptOrg}
AND C.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}
AND C.INPUT_TIME < #{endTime,jdbcType=TIMESTAMP}+ INTERVAL '1' DAY
ORDER BY O.DISTRICT_CODE ASC,CC.YISONG_TIME DESC

SELECT COUNT(1) FROM CASE_BASIC C,CRIME_CASE_FORM CC,CASE_STATE S, USER_ U,ORGANISE O WHERE C.CASE_ID=S.CASE_ID AND C.CASE_ID=CC.CASE_ID AND C.INPUTER= U.USER_ID AND U.ORG_ID=O.ORG_CODE AND C.CASE_STATE = 10 AND C.PROC_KEY ='chufaProc'
AND ${conditionFormula}
AND O.DISTRICT_CODE LIKE #{shortDistrictId}||'%'
AND C.CASE_NO LIKE CONCAT(CONCAT('%',#{caseNo}),'%')
AND C.CASE_NAME LIKE CONCAT(CONCAT('%',#{caseName}),'%')
AND O.ORG_PATH LIKE CONCAT(#{orgPath},'%')
AND O.ORG_CODE = #{orgId}
AND CC.ACCEPT_ORG = #{acceptOrg}
AND C.INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}
AND C.INPUT_TIME < #{endTime,jdbcType=TIMESTAMP}+ INTERVAL '1' DAY

SELECT COUNT(1) FROM CASE_BASIC C,CASE_STATE S,ORGANISE O 
WHERE C.INPUT_UNIT = O.ORG_CODE
AND C.CASE_ID = S.CASE_ID

AND o.DISTRICT_CODE LIKE regexp_replace(${regionId},'(00)+$')||'%'


AND  TO_CHAR(c.input_time,'yyyy')=${yearStr}


AND TO_CHAR(C.INPUT_TIME,'Q')=${quarterStr}


AND TO_CHAR(C.INPUT_TIME,'MM')=${monthStr}


AND S.CHUFA_STATE = ${chufaState}


AND S.YISONG_STATE = ${yisongState}

SELECT CB.CASE_ID,CB.CASE_NO,CB.CASE_NAME,CB.AMOUNT_INVOLVED,CB.CASE_STATE,O.ORG_NAME AS INPUT_UNIT_NAME,O.DISTRICT_CODE,CB.PROC_KEY,
PCF.IS_DESCUSS,PCF.CHUFA_TIMES,PCF.IS_SERIOUS_CASE,PCF.IS_BEYOND_EIGHTY,PCF.IS_LOWER_LIMIT_MONEY,PCF.IS_IDENTIFY,CB.INPUT_TIME
FROM CASE_BASIC CB,CASE_STATE CT,PENALTY_CASE_FORM PCF,ORGANISE O
WHERE CB.CASE_ID = CT.CASE_ID
AND CB.CASE_ID = PCF.CASE_ID
AND CB.INPUT_UNIT = O.ORG_CODE
AND CB.CASE_STATE = 2
AND CT.CHUFA_STATE = 2
AND O.DISTRICT_CODE LIKE CONCAT(#{districtCode},'%')

AND CB.CASE_NO LIKE CONCAT(CONCAT('%',#{caseNo}),'%')


AND CB.CASE_NAME LIKE CONCAT(CONCAT('%',#{caseName}),'%')


	AND O.ORG_PATH LIKE CONCAT(#{orgPath},'%')


 AND CB.AMOUNT_INVOLVED >= #{minAmountInvolvedS,jdbcType=DECIMAL}


 AND CB.AMOUNT_INVOLVED <= #{maxAmountInvolvedS,jdbcType=DECIMAL} 


 AND CB.INPUT_TIME >= #{minCaseInputTimeS,jdbcType=TIMESTAMP}


 AND CB.INPUT_TIME < (#{maxCaseInputTimeS,jdbcType=TIMESTAMP} + INTERVAL '1' DAY)


AND PCF.IS_DESCUSS = #{isDiscussCaseS}


 AND PCF.CHUFA_TIMES > #{chufaTimesS} 


AND PCF.IS_SERIOUS_CASE = #{isSeriousCaseS}


AND PCF.IS_BEYOND_EIGHTY = #{isBeyondEightyS}


  AND PCF.IS_IDENTIFY = #{isIdentifyS}


AND PCF.IS_LOWER_LIMIT_MONEY = #{isLowerLimitMoneyS}


 AND (1=2


     OR ( CB.AMOUNT_INVOLVED >= #{minAmountInvolved,jdbcType=DECIMAL}
        AND CB.AMOUNT_INVOLVED <= #{maxAmountInvolved,jdbcType=DECIMAL} )



     OR CB.AMOUNT_INVOLVED >= #{minAmountInvolved,jdbcType=DECIMAL} 


     OR CB.AMOUNT_INVOLVED <= #{maxAmountInvolved,jdbcType=DECIMAL} 




     OR (CB.INPUT_TIME >= #{minCaseInputTime,jdbcType=TIMESTAMP}
         AND CB.INPUT_TIME < (#{maxCaseInputTime,jdbcType=TIMESTAMP} + INTERVAL '1' DAY) )



     OR CB.INPUT_TIME >= #{minCaseInputTime,jdbcType=TIMESTAMP}


     OR CB.INPUT_TIME < (#{maxCaseInputTime,jdbcType=TIMESTAMP} + INTERVAL '1' DAY)



  OR PCF.IS_DESCUSS = #{isDiscussCase}


  OR PCF.CHUFA_TIMES > #{chufaTimes}


  OR PCF.IS_SERIOUS_CASE = #{isSeriousCase}


  OR PCF.IS_BEYOND_EIGHTY = #{isBeyondEighty}


  OR PCF.IS_LOWER_LIMIT_MONEY = #{isLowerLimitMoney}


  OR PCF.IS_IDENTIFY = #{isIdentify}

)

