SELECT COUNT(1) from ( SELECT T.CREATE_TIME, O.DISTRICT_CODE FROM CASE_TODO T, CASE_BASIC C, ORGANISE O WHERE T.ASSIGN_ORG = O.ORG_CODE AND T.CASE_ID = C.CASE_ID
AND O.ORG_PATH LIKE concat(#{orgPath},'%')
) WHERE DISTRICT_CODE LIKE #{districtCode}||'%' AND ${conditionFormula}

SELECT COUNT(1) from ( SELECT T.CREATE_TIME, O.DISTRICT_CODE FROM CASE_TODO T, CASE_BASIC C, ORGANISE O,CASE_STATE S WHERE T.ASSIGN_ORG = O.ORG_CODE AND T.CASE_ID = C.CASE_ID AND C.CASE_ID = S.CASE_ID AND S.JIEAN_STATE = 1
AND C.CASE_NAME LIKE CONCAT(CONCAT('%',#{caseName}),'%')
AND C.CASE_NO LIKE CONCAT(CONCAT('%',#{caseNo}),'%')
AND O.DISTRICT_CODE LIKE #{districtId}||'%'
AND O.ORG_PATH LIKE CONCAT(#{orgPath},'%')
AND O.ORG_CODE = #{orgId}
AND C.INPUT_TIME>=#{startTime,jdbcType=TIMESTAMP}
AND C.INPUT_TIME<(#{endTime,jdbcType=TIMESTAMP} + INTERVAL '1' DAY)
) WHERE ${conditionFormula}

SELECT CASE_ID,CASE_NO,CASE_NAME,CASE_STATE,INPUT_TIME,PROC_KEY,CREATE_TIME AS LATEST_POCESS_TIME,INPUT_UNIT_NAME,DISTRICT_CODE, FLOOR(TO_NUMBER(SYSDATE - (CREATE_TIME + 0)-60)) as TIME_OUT_DAYS from ( SELECT C.CASE_ID,C.CASE_NO,C.CASE_NAME,C.CASE_STATE,C.INPUT_TIME,C.PROC_KEY,T.CREATE_TIME, (SELECT ORG_NAME FROM ORGANISE WHERE ORG_CODE=C.INPUT_UNIT) AS INPUT_UNIT_NAME, O.DISTRICT_CODE FROM CASE_TODO T, CASE_BASIC C, ORGANISE O,CASE_STATE S WHERE T.ASSIGN_ORG = O.ORG_CODE AND T.CASE_ID = C.CASE_ID AND C.CASE_ID = S.CASE_ID AND S.JIEAN_STATE = 1
AND C.CASE_NAME LIKE CONCAT(CONCAT('%',#{caseName}),'%')
AND C.CASE_NO LIKE CONCAT(CONCAT('%',#{caseNo}),'%')
AND O.DISTRICT_CODE LIKE #{districtId}||'%'
AND O.ORG_PATH LIKE CONCAT(#{orgPath},'%')
AND O.ORG_CODE = #{orgId}
AND C.INPUT_TIME>=#{startTime,jdbcType=TIMESTAMP}
AND C.INPUT_TIME<(#{endTime,jdbcType=TIMESTAMP} + INTERVAL '1' DAY)
) WHERE ${conditionFormula} ORDER BY CREATE_TIME DESC

SELECT COUNT(1) from CASE_BASIC C,ORGANISE O,CRIME_CASE_FORM CC WHERE C.INPUT_UNIT = O.ORG_CODE AND C.CASE_ID = CC.CASE_ID AND C.CASE_STATE = 10
AND CC.ACCEPT_ORG = #{acceptOrg}
AND O.DISTRICT_CODE LIKE #{districtCode}||'%' AND ${conditionFormula}

		SELECT CB.CASE_ID,
           CB.CASE_NO,
           CB.CASE_NAME,
           CB.CASE_STATE,
           CB.INPUT_TIME,
           U.ORG_NAME AS INPUT_UNIT_NAME
   		 FROM CASE_BASIC CB,CASE_STATE CS,V_USER_ORGANISE_DISTRICT U
   		WHERE CB.CASE_ID=CS.CASE_ID
   		 AND CB.INPUTER=U.USER_ID

			AND U.DISTRICT_CODE LIKE #{districtCode}||'%' 


			AND U.ORG_PATH LIKE #{orgPath} ||'%'


			AND (INSTR(CB.CASE_NO,#{condition})> 0 OR INSTR(CB.CASE_NAME,#{condition})> 0)



			   AND CB.INPUT_TIME >= TO_DATE(#{startTimeApp},'yyyy-MM-dd')




			     AND CB.INPUT_TIME  < TO_DATE(#{endTimeApp},'yyyy-MM-dd') + INTERVAL '1' DAY



			AND CB.CASE_STATE =#{caseState}


			AND CS.YISONG_STATE =#{yisongState}


		    AND U.INDUSTRY_TYPE = #{industryType}


			AND CS.XINGZHENGLIAN_STATE =#{xingzhenglianState}


			AND CS.CHUFA_STATE =#{chufaState}


			AND CS.LIAN_STATE =#{lianState}


			AND CS.DAIBU_STATE =#{daibuState}


			AND CS.QISU_STATE =#{qisuState}


			AND CS.PANJUE_STATE =#{panjueState}


			AND CS.YISONG_STATE IN (2,3)

		ORDER BY U.DISTRICT_CODE ASC,CB.INPUT_TIME DESC

		 SELECT COUNT(CB.CASE_ID) 
		 FROM CASE_BASIC CB,CASE_STATE CS,V_USER_ORGANISE_DISTRICT U
    	 WHERE CB.CASE_ID=CS.CASE_ID
   		 AND CB.INPUTER=U.USER_ID

			AND U.DISTRICT_CODE LIKE #{districtCode}||'%' 


			AND U.ORG_PATH LIKE #{orgPath} ||'%'


			AND (INSTR(CB.CASE_NO,#{condition})> 0 OR INSTR(CB.CASE_NAME,#{condition})> 0)



			   AND CB.INPUT_TIME >= TO_DATE(#{startTimeApp},'yyyy-MM-dd')




			     AND CB.INPUT_TIME  < TO_DATE(#{endTimeApp},'yyyy-MM-dd') + INTERVAL '1' DAY



			AND CB.CASE_STATE =#{caseState}


			AND CS.YISONG_STATE =#{yisongState}


		    AND U.INDUSTRY_TYPE = #{industryType}


			AND CS.XINGZHENGLIAN_STATE =#{xingzhenglianState}


			AND CS.CHUFA_STATE =#{chufaState}


			AND CS.LIAN_STATE =#{lianState}


			AND CS.DAIBU_STATE =#{daibuState}


			AND CS.QISU_STATE =#{qisuState}


			AND CS.PANJUE_STATE =#{panjueState}


			AND CS.YISONG_STATE IN (2,3)

SELECT C.CASE_ID, C.CASE_NO,C.CASE_NAME,C.ANFA_TIME,C.CASE_DETAIL_NAME,C.UNDERTAKER,C.UNDERTAKER_TIME,C.UNDERTAKER_SUGGEST, C.RECORD_SRC, (SELECT D.DISTRICT_NAME FROM DISTRICT D WHERE D.DISTRICT_CODE=C.ANFA_ADDRESS) as ANFAADDRESSNAME, (SELECT U.USER_NAME FROM USER_ U WHERE U.USER_ID=P.ASSIGN_PERSON) AS TRANSACTPERSON, P.END_DATE AS TRANSACTTIME FROM CASE_BASIC C,CASE_STEP P WHERE C.CASE_ID=P.CASE_ID
AND C.CASE_ID =#{caseId}
AND P.STEP_ID =#{stepId}

