mapper.select:
SELECT C.ID,C.TITLE,C.INPUTER,U.USER_NAME userName,O.ORG_NAME orgName,C.INPUT_TIME,C.STATE,C.set_top ,C.case_no, (select count(*) from CASE_CONSULTATION_CONTENT where CASE_CONSULTATION_ID=c.id) as replyCount , (select max(PARTICIPATE_TIME) from CASE_CONSULTATION_CONTENT where CASE_CONSULTATION_ID=c.id and USER_ID =#{inputer} ) as replyTime, (select t.CASE_NAME from CASE_BASIC t where t.CASE_NO=C.case_no ) as caseName from CASE_CONSULTATION C,ORGANISE O,USER_ U WHERE C.INPUT_ORG_ID=O.ORG_CODE and U.USER_ID=C.INPUTER
AND C.TITLE like concat(concat('%',#{title}),'%')
AND C.STATE=#{state}
and INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}
and SET_TOP = #{setTop}
and INPUT_TIME < (#{endTime,jdbcType=TIMESTAMP} + interval '1' day)
and C.ID in (select P.CASE_CONSULTATION_ID FROM PARTICIPANTS P WHERE P.USER_ID=#{inputer,jdbcType=VARCHAR})
and C.ID in (select P.CASE_CONSULTATION_ID FROM case_consultation_content P WHERE P.USER_ID=#{inputer,jdbcType=VARCHAR})
and C.ID in (select P.CASE_CONSULTATION_ID FROM participants P WHERE P.USER_ID=#{inputer,jdbcType=VARCHAR}) and C.ID not in (select P.CASE_CONSULTATION_ID FROM case_consultation_content P WHERE P.USER_ID=#{inputer,jdbcType=VARCHAR})
and C.INPUTER =#{inputer,jdbcType=VARCHAR}
order by c.input_time desc

  	   SELECT count(*)
       FROM CASE_CONSULTATION C, ORGANISE O, USER_ U
            WHERE C.INPUT_ORG_ID = O.ORG_CODE
             and U.USER_ID = C.INPUTER

     		   AND C.TITLE like concat(concat('%',#{title}),'%')


     		   AND C.STATE=#{state}



			   and  INPUT_TIME >= #{startTime,jdbcType=TIMESTAMP}




			   and  SET_TOP = #{setTop}




			     and INPUT_TIME  < (#{endTime,jdbcType=TIMESTAMP} +   interval   '1'  day)




      	  	and C.ID in (select P.CASE_CONSULTATION_ID FROM case_consultation_content P WHERE P.USER_ID=#{inputer,jdbcType=VARCHAR})




      	  	and C.ID in (select P.CASE_CONSULTATION_ID FROM participants P WHERE P.USER_ID=#{inputer,jdbcType=VARCHAR})




      	  	and C.INPUTER =#{inputer,jdbcType=VARCHAR}
