mapper.select:
SELECT CT.TODO_ID, CT.CASE_ID, CT.CREATE_USER, CT.CREATE_TIME, CT.CREATE_ORG, CT.PROC_INST_ID, CT.PROC_DEF_ID, CT.ASSIGN_USER, CT.ASSIGN_ORG, CT.TASK_ACTION_ID, CT.TASK_ACTION_NAME, C.CASE_NAME,C.CASE_NO,C.ANFA_TIME,C.INPUT_TIME,C.CASE_STATE,C.AMOUNT_INVOLVED, (SELECT O.ORG_NAME FROM ORGANISE O WHERE O.ORG_CODE=C.INPUT_UNIT) AS CREATE_ORG_NAME , ART.ID_ AS TASK_ID, temp.action_id as action_id FROM CASE_TODO CT,CASE_BASIC C,ACT_RU_TASK ART,CASE_STATE CS, (select target_task_def_id,action_id,t.* from task_action,case_todo t where action_id(+)=t.TASK_ACTION_ID) temp WHERE ART.TASK_DEF_KEY_(+)=temp.target_task_def_id AND ART.proc_inst_id_(+)=temp.PROC_INST_ID AND ART.proc_def_id_(+)= temp.PROC_DEF_ID AND CT.CASE_ID = C.CASE_ID AND TEMP.TODO_ID = CT.TODO_ID AND CS.CASE_ID = C.CASE_ID AND CS.LIAN_SUP_STATE IS NULL
AND C.CASE_NAME LIKE concat(concat('%',#{caseName}),'%')
AND C.CASE_NO LIKE concat(concat('%',#{caseNo}),'%')
AND CT.ASSIGN_ORG = #{assignOrg}
AND C.INPUT_TIME>=#{startTime}
AND C.INPUT_TIME < #{endTime}+ interval '1' day
AND EXISTS ( SELECT 1 FROM CASE_BASIC S WHERE S.CASE_STATE = #{caseState} AND S.CASE_ID = C.CASE_ID)
AND C.IS_TURNOVER=#{isTurnover}
AND C.INPUT_UNIT = #{orgId}
AND C.case_state = #{caseStateFlag}
AND (C.case_state =0 or C.case_state=28)
AND C.case_id = #{caseId}
order by C.LATEST_POCESS_TIME desc

SELECT COUNT(1) FROM CASE_TODO CT,CASE_BASIC C,ACT_RU_TASK ART,CASE_STATE CS, (select target_task_def_id,action_id,t.* from task_action,case_todo t where action_id(+)=t.TASK_ACTION_ID) temp WHERE ART.TASK_DEF_KEY_(+)=temp.target_task_def_id AND ART.proc_inst_id_(+)=temp.PROC_INST_ID AND ART.proc_def_id_(+)= temp.PROC_DEF_ID AND CT.CASE_ID = C.CASE_ID AND TEMP.TODO_ID = CT.TODO_ID AND CS.CASE_ID = C.CASE_ID AND CS.LIAN_SUP_STATE IS NULL
AND C.CASE_NAME LIKE concat(concat('%',#{caseName}),'%')
AND C.CASE_NO LIKE concat(concat('%',#{caseNo}),'%')
AND CT.ASSIGN_ORG = #{assignOrg}
AND C.INPUT_TIME>=#{startTime}
AND C.INPUT_TIME< #{endTime}+ interval '1' day
AND EXISTS ( SELECT 1 FROM CASE_BASIC S WHERE S.CASE_STATE = #{caseState} AND S.CASE_ID = C.CASE_ID)
AND C.IS_TURNOVER=#{isTurnover}
AND C.INPUT_UNIT = #{orgId}
AND C.case_state = #{caseStateFlag}
AND (C.case_state =0 or C.case_state=28)
AND C.case_id = #{caseId}

SELECT COUNT(1) FROM CASE_TODO CT,TRANSFER_DETENTION TR,CASE_BASIC CB,ORGANISE O WHERE CT.CASE_ID=TR.CASE_ID AND TR.CASE_ID=CB.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE AND CB.CASE_STATE=30 AND CT.ASSIGN_ORG=#{assignOrg}
AND CB.CASE_NAME LIKE concat(concat('%',#{caseName}),'%')
AND CB.CASE_NO LIKE concat(concat('%',#{caseNo}),'%')
AND CB.INPUT_TIME>=#{startTime}
AND CB.INPUT_TIME < #{endTime}+ interval '1' day
AND O.ORG_PATH LIKE concat(#{orgPath},'%')

SELECT CB.CASE_ID,CB.CASE_NO,CB.CASE_NAME,CB.INPUT_TIME,O.ORG_NAME AS CREATE_ORG_NAME,CB.CASE_STATE,CS.LIAN_SUP_STATE,ART.ID_ AS TASK_ID FROM CASE_TODO CT,CASE_REQUIRE_NOLIAN_REASON CR,CASE_BASIC CB,ORGANISE O,CASE_STATE CS, ACT_RU_TASK ART, (SELECT TARGET_TASK_DEF_ID,ACTION_ID,T.* FROM TASK_ACTION,CASE_TODO T WHERE ACTION_ID(+)=T.TASK_ACTION_ID) TEMP WHERE CT.CASE_ID=CR.CASE_ID AND CB.CASE_ID=CR.CASE_ID AND CB.CASE_ID=CS.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE AND ART.TASK_DEF_KEY_(+)=temp.target_task_def_id AND ART.proc_inst_id_(+)=temp.PROC_INST_ID AND ART.proc_def_id_(+)= temp.PROC_DEF_ID AND TEMP.TODO_ID = CT.TODO_ID AND CS.LIAN_SUP_STATE IS NOT NULL
AND CT.ASSIGN_ORG = #{assignOrg}
AND CR.CREATE_ORG = #{createOrg}
AND CB.CASE_NAME LIKE concat(concat('%',#{caseName}),'%')
AND CB.CASE_NO LIKE concat(concat('%',#{caseNo}),'%')
AND CB.INPUT_TIME>=#{startTime}
AND CB.INPUT_TIME < #{endTime}+ interval '1' day
AND (CS.LIAN_SUP_STATE = 1 AND CS.EXPLAIN_STATE = 1)
AND ((CS.LIAN_SUP_STATE = 1 AND CS.EXPLAIN_STATE = 0) OR (CS.LIAN_SUP_STATE = 2 AND CS.LIAN_STATE = 4))
ORDER BY CB.INPUT_TIME DESC

SELECT COUNT(0) FROM CASE_TODO CT,CASE_REQUIRE_NOLIAN_REASON CR,CASE_BASIC CB,ORGANISE O,CASE_STATE CS, ACT_RU_TASK ART, (SELECT TARGET_TASK_DEF_ID,ACTION_ID,T.* FROM TASK_ACTION,CASE_TODO T WHERE ACTION_ID(+)=T.TASK_ACTION_ID) TEMP WHERE CT.CASE_ID=CR.CASE_ID AND CB.CASE_ID=CR.CASE_ID AND CB.CASE_ID=CS.CASE_ID AND CB.INPUT_UNIT=O.ORG_CODE AND ART.TASK_DEF_KEY_(+)=temp.target_task_def_id AND ART.proc_inst_id_(+)=temp.PROC_INST_ID AND ART.proc_def_id_(+)= temp.PROC_DEF_ID AND TEMP.TODO_ID = CT.TODO_ID AND CS.LIAN_SUP_STATE IS NOT NULL
AND CT.ASSIGN_ORG = #{assignOrg}
AND CR.CREATE_ORG = #{createOrg}
AND CB.CASE_NAME LIKE concat(concat('%',#{caseName}),'%')
AND CB.CASE_NO LIKE concat(concat('%',#{caseNo}),'%')
AND CB.INPUT_TIME>=#{startTime}
AND CB.INPUT_TIME < #{endTime}+ interval '1' day
AND (CS.LIAN_SUP_STATE = 1 AND CS.EXPLAIN_STATE = 1)
AND ((CS.LIAN_SUP_STATE = 1 AND CS.EXPLAIN_STATE = 0) OR (CS.LIAN_SUP_STATE = 2 AND CS.LIAN_STATE = 4))
ORDER BY CB.INPUT_TIME DESC
