INSERT INTO CLUE_INFO (CLUE_ID, CLUE_NO, CLUE_RESOURCE, 
  ADDRESS, CREATE_ORG, CREATE_USER, 
  CREATE_TIME, CLUE_STATE, CONTENT
  )
VALUES (#{clueId,jdbcType=DECIMAL}, #{clueNo,jdbcType=VARCHAR}, #{clueResource,jdbcType=DECIMAL}, 
  #{address,jdbcType=VARCHAR}, #{createOrg,jdbcType=DECIMAL}, #{createUser,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{clueState,jdbcType=DECIMAL}, #{content,jdbcType=CLOB}
  )

mapper.select:
  	SELECT COUNT(1) 
  	FROM clue_info CI


        AND CI.CLUE_NO LIKE concat(concat('%',#{clueNo,jdbcType=VARCHAR}),'%')


        AND CI.CLUE_RESOURCE = #{clueResource,jdbcType=DECIMAL}


        AND CI.ADDRESS = #{address,jdbcType=VARCHAR}


        AND CI.CREATE_USER = #{createUser,jdbcType=NUMERIC}


        AND CI.CREATE_ORG = #{createOrg,jdbcType=NUMERIC}


    	AND  CI.CREATE_TIME>=#{startTime} 


	    AND  CI.CREATE_TIME< #{endTime}+ interval '1' day


        AND CI.CLUE_STATE = #{clueStateFlag}


         AND CI.CREATE_DISTRICTCODE LIKE concat(#{createDistrictcode,jdbcType=VARCHAR},'%')


         AND CI.CREATE_ORG IN (SELECT O.ORG_CODE FROM ORGANISE O WHERE O.ORG_PATH LIKE concat(#{orgPath,jdbcType=VARCHAR},'%') )


        AND CI.CLUE_STATE = 1 order by CI.CREATE_TIME asc



        AND (CI.CLUE_STATE = 2 OR CI.CLUE_STATE = 3) ORDER BY CI.CLUE_STATE ASC,CI.CREATE_TIME ASC


        AND (CI.DISTRIBUTE_USER_ID = #{distributeUserId,jdbcType=VARCHAR})

  	SELECT ,
  	(SELECT u.USER_NAME FROM USER_ u WHERE CI.CREATE_USER=u.USER_ID) as CREATE_USER_NAME,
  	(SELECT o.ORG_NAME FROM ORGANISE o WHERE o.ORG_CODE= CI.CREATE_ORG) as CREATE_ORG_NAME
  	FROM CLUE_INFO CI


        AND CI.CLUE_NO LIKE concat(concat('%',#{clueNo,jdbcType=VARCHAR}),'%')


        AND CI.CLUE_RESOURCE = #{clueResource,jdbcType=DECIMAL}


        AND CI.ADDRESS = #{address,jdbcType=VARCHAR}


        AND CI.CREATE_USER = #{createUser,jdbcType=NUMERIC}


        AND CI.CREATE_ORG = #{createOrg,jdbcType=NUMERIC}


    	AND  CI.CREATE_TIME>=#{startTime} 


	    AND  CI.CREATE_TIME< #{endTime}+ interval '1' day


        AND CI.CLUE_STATE = #{clueStateFlag}


         AND CI.CREATE_DISTRICTCODE LIKE concat(#{createDistrictcode,jdbcType=VARCHAR},'%')


         AND CI.CREATE_ORG IN (SELECT O.ORG_CODE FROM ORGANISE O WHERE O.ORG_PATH LIKE concat(#{orgPath,jdbcType=VARCHAR},'%') )


        AND CI.CLUE_STATE = 1 ORDER BY CI.CREATE_TIME DESC



        AND (CI.CLUE_STATE = 2 OR CI.CLUE_STATE = 3) ORDER BY CI.CLUE_STATE ASC,CI.CREATE_TIME DESC


        AND (CI.DISTRIBUTE_USER_ID = #{distributeUserId,jdbcType=VARCHAR})

  	SELECT ,
  	(SELECT u.USER_NAME FROM USER_ u WHERE CI.CREATE_USER=u.USER_ID) as CREATE_USER_NAME,
  	(SELECT o.ORG_NAME FROM ORGANISE o WHERE o.ORG_CODE= CI.CREATE_ORG) AS CREATE_ORG_NAME
  	FROM CLUE_INFO CI


        AND CI.CLUE_NO LIKE concat(concat('%',#{clueNo,jdbcType=VARCHAR}),'%')


        AND CI.CREATE_ORG = #{createOrg,jdbcType=DECIMAL}


        AND CI.CLUE_STATE = #{clueState}


