mapper.select:
	  SELECT R.*,O.ORG_NAME AS sendOrgName 
  	  FROM INSTRUCTION_RECEIVE R, ORGANISE O
  	  WHERE R.SEND_ORG=O.ORG_CODE 

       AND R.TITLE like '%'||#{title,jdbcType=VARCHAR}||'%'


       AND  R.SEND_TIME = #{sendTime,jdbcType=DATE}


		AND R.STATUS = #{status,jdbcType=DECIMAL}


		AND  R.RECEIVE_ORG = #{receiveOrg,jdbcType=VARCHAR}    

     ORDER BY  R.SEND_TIME desc

		SELECT COUNT(1)  FROM INSTRUCTION_RECEIVE R, ORGANISE O
  	  WHERE R.SEND_ORG=O.ORG_CODE

       AND R.TITLE like '%'||#{title,jdbcType=VARCHAR}||'%'


       AND  R.SEND_TIME = #{sendTime,jdbcType=DATE}


		AND R.STATUS = #{status,jdbcType=DECIMAL}


		AND R.RECEIVE_ORG = #{receiveOrg,jdbcType=VARCHAR}

  	SELECT 

    ,

    FROM INSTRUCTION_RECEIVE
    WHERE RECEIVE_ORG = #{receiveOrg,jdbcType=VARCHAR}

		AND STATUS = #{status,jdbcType=DECIMAL}


        AND RECEIVE_ORG = #{receiveOrg,jdbcType=VARCHAR}

SELECT ,(SELECT ORG_NAME FROM ORGANISE WHERE org_code = receive_org) as receive_org_name
FROM INSTRUCTION_RECEIVE
WHERE INSTRUCTION_ID = #{instructionId}

 SELECT  COUNT(1) SEND_NUM,
    NVL(SUM(CASE WHEN REPLY_ID IS NOT NULL  THEN 1 ELSE 0 END),0) AS REPLY_NUM,
    NVL(SUM(CASE WHEN REPLY_ID IS NULL  THEN 1 ELSE 0 END),0) AS NO_REPLY_NUM,
    NVL(SUM(CASE WHEN STATUS =2 AND REPLY_ID IS NULL THEN 1 ELSE 0 END),0) AS DO_NUM
   FROM (SELECT REPLY_ID,STATUS,SEND_TIME,SEND_ORG,RECEIVE_ORG FROM 
   INSTRUCTION_RECEIVE R
   LEFT JOIN INSTRUCTION_REPLY P
   ON R.INSTRUCTION_ID = P.INSTRUCTION_ID
   AND R.RECEIVE_ORG = P.ORG_CODE
   ) A
   WHERE SEND_ORG = #{sendOrg}

   AND TO_CHAR(SEND_TIME,'YYYY')=${yearStr}


	AND TO_CHAR(SEND_TIME,'Q')=${quarterStr} 


	AND TO_CHAR(SEND_TIME,'MM')=${monthStr}

   SELECT * FROM
   (
  SELECT RECEIVE_ORG,
  (SELECT ORG_NAME FROM ORGANISE WHERE ORG_CODE = RECEIVE_ORG) AS ORG_NAME,
  COUNT(1) SEND_NUM,
    NVL(SUM(CASE WHEN REPLY_ID IS NOT NULL  THEN 1 ELSE 0 END),0) AS REPLY_NUM,
    NVL(SUM(CASE WHEN REPLY_ID IS NULL  THEN 1 ELSE 0 END),0) AS NO_REPLY_NUM,
    NVL(SUM(CASE WHEN STATUS =2 AND REPLY_ID IS NULL THEN 1 ELSE 0 END),0) AS DO_NUM
   FROM (SELECT REPLY_ID,STATUS,SEND_TIME,SEND_ORG,RECEIVE_ORG FROM 
   INSTRUCTION_RECEIVE R
   LEFT JOIN INSTRUCTION_REPLY P
   ON R.INSTRUCTION_ID = P.INSTRUCTION_ID
   AND R.RECEIVE_ORG = P.ORG_CODE
   ) A
   WHERE SEND_ORG = #{sendOrg}

	   AND TO_CHAR(SEND_TIME,'YYYY')=${yearStr}


		AND TO_CHAR(SEND_TIME,'Q')=${quarterStr} 


		AND TO_CHAR(SEND_TIME,'MM')=${monthStr}

   GROUP BY A.RECEIVE_ORG
   ) T WHERE T.ORG_NAME IS NOT NULL ORDER BY T.RECEIVE_ORG
