select 

from ORGANISE
where ORG_CODE = #{orgCode,jdbcType=DECIMAL}

       select * from organise n
      where is_dept=0
        and org_code=#{orgCode} 
        and district_code = #{districtCode}
        and org_type not in

				#{listItem}

	select 

  from ORGANISE
  where SI_ORG_ID = #{siOrgId,jdbcType=VARCHAR}

    select b.* from
	(select * from organise o,task_bind t 
				where
				t.assign_target=o.org_type
				and o.is_dept=1
				and t.proc_def_id=#{procDefId}
				and t.task_def_id=#{taskDefId}
                and o.district_code = #{districtCode}
	) b
	where 
	exists( select * from task_assign a where a.dept_id=b.org_code and b.is_dept=1 
	and a.proc_def_id=#{procDefId} and a.task_def_id=#{taskDefId})
    or b.is_dept=0

SELECT COUNT(1) FROM ORGANISE WHERE 
IS_DEPT = 0
AND ORG_CODE != #{currOrg} 

AND DISTRICT_CODE= #{districtCode} 


	AND ORG_TYPE =#{orgType}

	SELECT * FROM ORGANISE WHERE 
	IS_DEPT = 0 
	AND ORG_CODE != #{currOrg}

   AND DISTRICT_CODE = #{districtCode} 


		AND ORG_TYPE =#{orgType}

	  ORDER BY DISTRICT_CODE

     	select * from ORGANISE o where 1=1

		  		and o.org_code in

		  			#{item}

		select O.ORG_CODE, O.ORG_NAME, O.ORG_TYPE, O.DISTRICT_CODE, O.UP_ORG_CODE,O.INDUSTRY_TYPE,IS_LEAF 
		from ORGANISE O
		where 1 = 1

       	 	and o.industry_type = #{industryType,jdbcType=VARCHAR} 


       		and o.UP_ORG_CODE = #{upOrgCode,jdbcType=VARCHAR}

		order by district_code

select count(1) from organise o,district d 
 where o.district_code = d.district_code
   and o.org_path like concat((select org_path from organise where org_code = #{orgCode}),'%') 
 and o.district_code like #{districtCode}||'%'
 and o.org_type = #{orgType } 
 and o.ORG_PATH LIKE #{orgPath }||'%'
 and o.UP_ORG_CODE = #{upOrgCode } 
 and o.industry_type = #{industryType }
 and d.jb = #{jb }
 and o.ORG_CODE <> #{currOrg}


 		and o.UP_ORG_CODE =#{currOrg}



 	and o.district_code <> #{currDistrict}


 	and o.district_code in (select district_code from district where up_District_Code = #{upDistrictCode})


 	and o.is_dept = 0

SELECT O.* FROM ORGANISE O,DISTRICT D 
 WHERE O.DISTRICT_CODE = D.DISTRICT_CODE
   AND O.ORG_PATH LIKE CONCAT((SELECT ORG_PATH FROM ORGANISE WHERE ORG_CODE = #{orgCode}),'%') 
 AND O.DISTRICT_CODE LIKE #{districtCode}||'%'
 AND O.ORG_TYPE = #{orgType } 
 AND O.ORG_PATH LIKE #{orgPath }||'%' 
 AND O.ORG_CODE = #{upOrgCode }
 AND O.INDUSTRY_TYPE = #{industryType }
 AND D.JB = #{jb }
 AND O.ORG_CODE <> #{currOrg}


 		AND O.UP_ORG_CODE =#{currOrg}



 AND O.DISTRICT_CODE <> #{currDistrict}


 AND O.DISTRICT_CODE IN (SELECT DISTRICT_CODE FROM DISTRICT WHERE UP_DISTRICT_CODE = #{upDistrictCode})

 AND O.IS_DEPT = 0


 	ORDER BY DECODE(O.ORG_TYPE,5,5) ,O.ORG_CODE


 	ORDER BY O.ORG_CODE

SELECT O.*,(SELECT COUNT(1) FROM ORGANISE O2 WHERE O2.UP_ORG_CODE=O.ORG_CODE) chirdNum FROM ORGANISE O WHERE ORG_TYPE = #{orgType}
AND DISTRICT_CODE = #{districtCode}
AND UP_ORG_CODE = #{orgCode}

SELECT O.*,(SELECT COUNT(1) FROM ORGANISE O2 WHERE O2.UP_ORG_CODE=O.ORG_CODE) chirdNum FROM ORGANISE O WHERE ORG_TYPE=#{orgType}
AND DISTRICT_CODE = (SELECT DISTRICT_CODE FROM ORGANISE WHERE ORG_CODE = #{orgCode,jdbcType=DECIMAL})
AND  (SELECT COUNT(1) FROM ORGANISE O2 WHERE O2.UP_ORG_CODE=O.ORG_CODE) > 0

