mapper.select:
SELECT P.CASE_ID,P.PENALTY_FILE_NO,P.UNDERTAKER,P.CHUFA_TIME,P.CHUFA_TIMES,P.CHUFA_TYPE_NAME, P.IS_DESCUSS,P.IS_SERIOUS_CASE,P.IS_BEYOND_EIGHTY,P.IS_LOWER_LIMIT_MONEY,P.IS_DQDJ, (SELECT D1.NAME FROM DQDJ_CATEGORY D1, DQDJ_CASE_CATEGORY D2 WHERE D1.CATEGORY_ID=D2.CATEGORY_ID AND CASE_ID=#{caseId})AS dqdjTypeName, P.GOODS_INVOLVED,P.GOODS_COUNT,P.GOODS_DISPOSE_RESULT,P.IS_IDENTIFY, (SELECT DT_NAME FROM DICTIONARY_ WHERE GROUP_CODE='identifyType' AND DT_CODE=P.IDENTIFY_TYPE) AS IDENTIFY_TYPE_NAME, P.IDENTIFY_ORG,P.IDENTIFY_PERSON,P.IDENTIFY_TIME,P.IDENTIFY_RESULT, P.CHUFA_BASIS,P.CHUFA_RESULT,P.MEMO,P.PENALTY_FILE_ID, (SELECT ATTACHMENT_NAME FROM CASE_ATTACHMENT WHERE ID=P.PENALTY_FILE_ID) AS PENALTY_FILE_NAME, (SELECT U.USER_NAME FROM USER_ U WHERE U.USER_ID=C.ASSIGN_PERSON) AS TRANSACTPERSON, C.END_DATE AS TRANSACTTIME FROM PENALTY_CASE_FORM P,CASE_STEP C WHERE P.CASE_ID=C.CASE_ID
AND P.CASE_ID =#{caseId}
AND C.STEP_ID =#{stepId}
