mapper.select:
  	select *
	  from (select wa.article_id,
	               wa.article_title,
	               wa.article_time 
	          from web_article wa, web_programa wp
	         where wa.programa_id = wp.programa_id
	           and wp.home_location =#{homeLocation}
	           order by WA.SORT ASC, wa.article_time desc)
	 where rownum <= #{size}

  	select *
	  from (select wa.article_id,
	               wa.article_title,
	               wa.article_time,
	               wa.image_path
	          from web_article wa, web_programa wp
	         where wa.programa_id = wp.programa_id
	           and wp.home_location =#{homeLocation}
	           order by WA.SORT ASC, wa.article_time desc)
	 where rownum <= #{size}

SELECT A.*,U.USER_NAME userName,p.PROGRAMA_NAME programaName,at.TYPE_NAME typeName,(select o.org_name from organise o where o.org_code = U.org_id and U.user_id = A.USER_ID) as orgName FROM WEB_ARTICLE A,USER_ U,web_programa p,web_article_type at
A.USER_ID = U.USER_ID and A.PROGRAMA_ID = P.PROGRAMA_ID and A.TYPE_ID = at.TYPE_ID
and A.USER_ID=#{userId}
and A.ARTICLE_TITLE like concat(concat('%',#{articleTitle}),'%')
and A.PROGRAMA_ID=#{programaId}
and A.TYPE_ID=#{typeId}
and EXISTS (SELECT * FROM ORGANISE O WHERE O.DISTRICT_CODE LIKE CONCAT(#{districtCode},'%') AND U.ORG_ID = O.ORG_CODE)
order by A.SORT ASC, A.ARTICLE_TIME desc

SELECT count(*) FROM WEB_ARTICLE A,USER_ U,web_programa p,web_article_type at
A.USER_ID = U.USER_ID and A.PROGRAMA_ID = P.PROGRAMA_ID and A.TYPE_ID = at.TYPE_ID
and A.USER_ID=#{userId}
and A.ARTICLE_TITLE like concat(concat('%',#{articleTitle}),'%')
and A.PROGRAMA_ID=#{programaId}
and A.TYPE_ID=#{typeId}
and EXISTS (SELECT * FROM ORGANISE O WHERE O.DISTRICT_CODE LIKE CONCAT(#{districtCode},'%') AND U.ORG_ID = O.ORG_CODE)

select wa.article_id, wa.article_title, wa.article_time from web_article wa where wa.programa_id = #{programaId}
and wa.ARTICLE_TITLE like concat(concat('%',#{title}),'%')
order by wa.article_time desc
